function ed_PYVND_OnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam)
{
	if(nChar == 13){
		ds_BHZE602Q.ClearData();
		if(length(trim(obj.Text)) < 1) return;
		//득의선Check
		http.usewaitcursor = false;
		http.Sync 	= true;
		var sKind		= "getBHZ600";		
		var sMethodName = "service::pscomlib/selectBHZ600.do"; 
		var sInDataSet  = "";
		var sOutDataSet = "ds_BHZ600PF=ds_BHZ600PF";
		var sArgument   = "";
			sArgument  += " LIB1=" + quote(Lib1);
			sArgument  += " LIB2=" + quote(Lib2);
			sArgument  += " COMPS=" + quote(ComboBoxPlant.Value); //사업부
			sArgument  += " PYVND=" + quote(ed_PYVND.Text);
		Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
		if (ds_BHZ600PF.GetRowCount() < 1){
			alert(setPopMessage("1502"));
			setMessage("1502");	
			ed_PYVND.SetFocus();
			return;
		}
		st_PYVND.Text=ds_BHZ600PF.GetColumn(0,"CUSNM");
		http.usewaitcursor = true;	
		fn_Search();
	}
}
function ed_PYVND_OnCharChanged(obj,strPreText,strPostText)
{
	st_PYVND.Text = "";
	ds_BHZE602Q.ClearData();
}
