function getComCode(){
	http.Sync 	= true;
	var sKind		= "getComCode";						
	var sMethodName = "service::login/selectComCode.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ComCode_PLNT=ds_ComCode_PLNT";
	var sArgument   = "";
		sArgument   += " CLASS='PLNT'";
		sArgument   += " CODE1=" + quote(company);		
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	cb_searchCodeName1.Value = comps;
}

/**********************************************************************
 * 내용설명 : 단가소급처리 대상품번 전표번호 조회(MST040PF)
 **********************************************************************/
function getMST040PF(){
	http.Sync 	= true;
	var sKind		= "getMST040PF";						
	var sMethodName = "service::pscomlib/selectMST040PF.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_MST040PF=ds_MST040PF";
	var sArgument   = "";
		sArgument += " LIB2=" + quote(Lib2);
		sArgument += " COMPS=" + quote(cb_searchCodeName1.Value);
		sArgument += " MAJCD=" + quote("BIDC251");		
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	if (ds_MST040PF.rowcount == 0){
		alert(setPopMessage("1444"));  // [경고] 시스템에 문제가 발생하였습니다. 디오네 종료 후 재가동바랍니다.
		setMessage("1444");	
	}
}
/**********************************************************************
 * 내용설명 : 단가소급처리 대상품번 전표번호 저장(MST040PF)
 **********************************************************************/
 function setMst040pf()
 {
	http.Sync = true;
	var sKind		= "setmst040";
	var sMethodName = "service::pscomlib/updateMST040.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "";    		 								// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
	sArgument += " LIB2=" + quote(Lib2);
	sArgument += " COMPS=" + quote(cb_searchCodeName1.Value);
	sArgument += " MAJCD=" + quote("BIDC251");
	sArgument += " MJINT=" + quote(ed_SGBJP.Text);
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");

}
/********************************************************************** 
 * 내용설명 : 화면조회
 * 주요변수 : 
 **********************************************************************/ 
function getBIDC251(){

	if(length(trim(ed_SGBJP.Text)) < 1){
		alert(setPopMessage("4012"));  //[[확인]  입력바랍니다.
		setMessage("4012");	
		ed_SGBJP.SetFocus();
		return;
	}
	if(length(trim(st_VNAME.Text)) < 1){
		alert(setPopMessage("4674"));  //[[확인]  입력바랍니다.
		setMessage("4674");	
		ed_SGBVN.SetFocus();
		return;
	}

	//조회
	var sKind		= "getBIDC251";	
	var sMethodName = "service::psimmlib/selectBIDC251.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_BIDC251=ds_BIDC251";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";

	sArgument += "@LIB1=" + quote(Lib1);
	sArgument += "@LIB2=" + quote(Lib2);
	sArgument += "@COMPS=" + quote(cb_searchCodeName1.Value);
	sArgument += "@SGJNO=" + quote(ed_P_SGBJP.Text + right("0000000"+ed_SGBJP.Text,7));
	sArgument += "@SGVND=" + quote(trim(ed_SGBVN.Text));
	var str = dialog("Common::PGMLOAD_IMM.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");	

	if (str == 'T'){  //정상조회
		alert(setPopMessage("1004"));
		setMessage("1004");
		MainGrid.SetFocus();
		ds_BIDC251.rowposition = 0;
		MainGrid.SetCellPos(3);
	}else if (str == 'B'){   //data값이 없을때
		alert(setPopMessage("1003"));  //   [알림] 조회 된 데이터가 없습니다.
		setMessage("1003");	
	}else if (str == 'E'){   //error발생시
		alert(setPopMessage("1002"));
		setMessage("1002");	
	}
}

/********************************************************************** 
 * 내용설명 : 저장
 * 주요변수 : 
 **********************************************************************/ 
 function setBIDC251(){
	if(length(trim(ed_SGBJP.Text)) < 1){
		alert(setPopMessage("4012"));  //[[확인]  입력바랍니다.
		setMessage("4012");	
		ed_SGBJP.SetFocus();
		return;
	}
	if(length(trim(st_VNAME.Text)) < 1){
		alert(setPopMessage("4674"));  //[[확인]  입력바랍니다.
		setMessage("4674");	
		ed_SGBVN.SetFocus();
		return;
	}

	ds_BIDC251.fireEvent = false;
	ds_BIDC251.UpdateControl = false;
	//var c_SGBQY = ds_BIDC251.GetColumn(i,"SGSDT");
		//var c_SGBST = ds_BIDC251.GetColumn(i,"SGBST");
	for(i=0;i<ds_BIDC251.rowcount;i++){
		var c_SGPNO = trim(ds_BIDC251.GetColumn(i,"SGPNO"));
		var c_SGSDT = ds_BIDC251.GetColumn(i,"SGSDT");
		var c_SGEDT = ds_BIDC251.GetColumn(i,"SGEDT");
		if (c_SGPNO == null || c_SGPNO == ""){
			ds_BIDC251.SetRowType(i,"normal");
		}else{
			if(length(c_SGSDT) != 8){
				alert(setPopMessage("1504")); //[확인] 시작일자를 확인바랍니다.
				setMessage("1504");
				ds_BIDC251.rowposition = i;
				MainGrid.SetCellPos(3);
				return;
			}
			if(length(c_SGEDT) != 8){
				alert(setPopMessage("1505")); //[확인] 종료일자를 확인바랍니다.
				setMessage("1505");
				ds_BIDC251.rowposition = i;
				MainGrid.SetCellPos(4);
				return;
			}
			if(toNumber(c_SGSDT) > toNumber(c_SGEDT)){
				alert(setPopMessage("1503"));  //[확인] 시작일자가 종료일자보다 클 수는 없습니다.
				setMessage("1503");
				ds_BIDC251.rowposition = i;
				MainGrid.SetCellPos(3);
				return;
			}
		}
	}
	ds_BIDC251.fireEvent = true;

	var sKind		= "SAVE_BIDC251";						
	var sMethodName = "service::psimmlib/saveBIDC251.do";
	var sInDataSet  = "ds_BIDC251=ds_BIDC251:U";
	var sOutDataSet = "ds_BIDC251_CHECK=ds_BIDC251_CHECK";
	var sArgument   = "";
	sArgument += " LIB2=" + quote(Lib2);
	sArgument += " ADDID=" + quote(G_ds_user.GetColumn(0,"USER_ID"));
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");

 }

