/********************************************************************** 
 * 내용설명 : Transaction 리턴후 실행 함수
 * 주요변수 : 
 * 			- sMethodName	Transaction 고유 ID
 * 			- ErrorCode 	에러코드
 * 			- ErrorMSG 		에러 메세지
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	if (svcId = "SAVE_BIDC251"){
		if (ds_BIDC251_CHECK.GetRowCount() > 0){			
			for(var i=0;i<ds_BIDC251_CHECK.GetRowCount();i++){		
				alert(ds_BIDC251_CHECK.GetColumn(i,"SGBJP") + " , "
				    + ds_BIDC251_CHECK.GetColumn(i,"SGBPN") + " ="
					+ ds_BIDC251_CHECK.GetColumn(i,"RTMSG"));	
			}
			//return;		
		}
	}
	// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");

	}else {
		if(toNumber(ed_SGBJP.Text) > toNumber(st_BSGBJP.Text)){
			setMst040pf(); //전표번호 저장(MST040PF)
		}
		alert(setPopMessage("1001"));
		setMessage("1001");	
		//getBIDC251();
		getMST040PF();
		st_BSGBJP.Text = right("0000000"+ds_MST040PF.GetColumn(0,"MJINT"),7);
		//ed_SGBJP.Text = toNumber(ds_MST040PF.GetColumn(0,"MJINT"))+1;
		//ed_SGBJP.Text = "";
		//ed_SGBVN.Text = "";
		//st_VNAME.Text = "";
		ed_SGBJP.SetFocus();
	}
}

