function fn_Input()
{
	if(length(trim(ed_SGBJP.Text)) < 1){
		alert(setPopMessage("4012"));  //[[확인] 전표번호를 입력바랍니다.
		setMessage("4012");	
		ed_SGBJP.SetFocus();
		return;
	}
	if(checkInsertCount("ds_BIDC251")== false) return;

	getBIDC251_DCHK();
	ed_Action.Text = "A";
	if (toNumber(ds_BIDC251_DCHK.GetColumn(0,"CNT")) != 0){
		if(confirm("이미 등록된 자료입니다. 기등록된 자료에 추가하시겠습니까? ") == true){
			var cnt1 =ds_BIDC251.rowcount;
			if(cnt1<1) getBIDC251();  //조회->수정->추가클릭시에는 다시조회하지 않는다.
			var cnt2 =ds_BIDC251.rowcount;
			ds_BIDC251.FireEvent=false;  //데이터셋의 이벤트가 발생하지 않도록 셋팅
			for (var i=cnt2;i<18;i++){
				ds_BIDC251.AddRow();
				ds_BIDC251.SetColumn(ds_BIDC251.Row, "COMPS", cb_searchCodeName1.Value); // default값 설정
				ds_BIDC251.SetColumn(ds_BIDC251.Row, "SGJNO", ed_P_SGBJP.Text + right("0000000"+ed_SGBJP.Text,7)); // default값 설정
				ds_BIDC251.SetColumn(ds_BIDC251.Row, "SGVND", trim(ed_SGBVN.Text));// default값 설정 ----업체코드
			}
			ds_BIDC251.FireEvent=true;
			ds_BIDC251.rowposition = cnt2;
			MainGrid.SetCellPos(0);
		}else{
			//alert(setPopMessage("1520"));  //[경고] 이미 등록된 자료입니다.
			//setMessage("1520");	
			ed_SGBJP.SetFocus();
			return;
		}
	}else{
		if(toNumber(ed_SGBJP.Text) != toNumber(right(st_BSGBJP.Text,5))+1){
			alert(setPopMessage("4012"));  //[[확인] 전표번호를 입력바랍니다.
			setMessage("4012");	
			ed_SGBJP.SetFocus();
			return;
		}
		ds_BIDC251.FireEvent=false;  //데이터셋의 이벤트가 발생하지 않도록 셋팅
		for (var i=0;i<18;i++){
			ds_BIDC251.AddRow();
			ds_BIDC251.SetColumn(ds_BIDC251.Row, "COMPS", cb_searchCodeName1.Value); // default값 설정
			ds_BIDC251.SetColumn(ds_BIDC251.Row, "SGJNO", ed_P_SGBJP.Text + right("0000000"+ed_SGBJP.Text,7)); // default값 설정
			ds_BIDC251.SetColumn(ds_BIDC251.Row, "SGVND", trim(ed_SGBVN.Text));// default값 설정 ----업체코드
			ds_BIDC251.SetColumn(ds_BIDC251.Row, "RPCST", 0);// default값 설정 ---구입단가(2018.10.24추가)
			ds_BIDC251.SetColumn(ds_BIDC251.Row, "SGCST", 0);// default값 설정 ----소급단가(2018.10.24추가)
		}
		MainGrid.SetFocus();
		ds_BIDC251.rowposition = 0;
		MainGrid.SetCellPos(0);
		ds_BIDC251.FireEvent=true;	
	}
}

