function fn_total()
{
	var total;
	for(var i=0;i<ds_BIDC251.GetRowCount();i++){
		//(toNumber(SGCST)-toNumber(RPCST))*toNumber(RPQTY)
		var column1 = toNumber(ds_BIDC251.GetColumn(i,"SGCST"));
		var column2 = toNumber(ds_BIDC251.GetColumn(i,"RPCST"));
		var column3 = toNumber(ds_BIDC251.GetColumn(i,"RPQTY"));
		total += (column1-column2)*column3;
	}
	return total;
}

