function ds_BIDC251_CanColumnChange(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	http.usewaitcursor = false;  //transaction호출후 저장이벤트가 발생되지 않아서...
	if( strColumnID == "SGPNO" ){
		if (length(varNewValue) < 1) {
			alert(setPopMessage("2501"));  //[경고] 필수입력항목입니다. (품번)
			setMessage("2501");	
			return false;
		}
		//중복Check
		if(ds_BIDC251.FindRow("SGPNO", trim(varNewValue)) >= 0){
			alert(setPopMessage("1520"));  //[경고] 이미 등록된 자료입니다.
			setMessage("1520");	
			return false;
		}
		//품번Check,조회
		http.Sync = true;
		var sKind		= "getBM008PR";		
		var sMethodName = "service::pscomlib/selectBM008PR.do"; 
		var sInDataSet  = "";
		var sOutDataSet = "ds_BM008PR=ds_BM008PR";
		var sArgument   = "";
			sArgument  += " LIB1=" + quote(Lib1);
			sArgument  += " ITNBR=" + quote(varNewValue);
		Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
		if (ds_BM008PR.GetRowCount() < 1){
			alert(setPopMessage("1509"));  // [경고] Item Master에 미등록된 품번입니다.
			setMessage("1509");	
			return false;
		}
		can_ITDSC = ds_BM008PR.GetColumn(0,"ITDSC");
		can_ITCLS = substr(ds_BM008PR.GetColumn(0,"ITCLS"),0,1);
		//구매단가Check
		http.Sync = true;
		var sKind		= "getBM008PR";		
		var sMethodName = "service::pscomlib/selectCM001PR.do"; 
		var sInDataSet  = "";
		var sOutDataSet = "ds_CM001PR=ds_CM001PR";
		var sArgument   = "";
			sArgument  += " LIB1=" + quote(Lib1);
			sArgument  += " PRTNO=" + quote(varNewValue);
			sArgument  += " SPCOD=" + quote(trim(ed_SGBVN.Text));
			sArgument  += " IMADATE=" + quote(nowDate);
		Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
		if (ds_CM001PR.GetRowCount() < 1){
			alert(setPopMessage("1511"));  // [경고] Purchase Price Master에 미등록된 품번입니다.
			setMessage("1511");	
			return false;
		}
		var sDate = ds_BIDC251.GetColumn(nRow,"SGSDT");
		var eDate = ds_BIDC251.GetColumn(nRow,"SGEDT");
		if(length(sDate) == 8 && length(eDate) ==8){
			inputQtyCheck(varNewValue,sDate,eDate);  //당월,전년,금년 입고수량CHECK
		}
	}
	if( strColumnID == "SGSDT" ){
		if (length(varNewValue) < 8 || toNumber(nowDate) < toNumber(varNewValue)) {
			alert(setPopMessage("1504"));  //[확인] 시작일자를 확인바랍니다.
			setMessage("1504");	
			return false;
		}
		var pNo = ds_BIDC251.GetColumn(nRow,"SGPNO");
		var eDate = ds_BIDC251.GetColumn(nRow,"SGEDT");
		if(length(eDate) ==8 && toNumber(varNewValue) > toNumber(eDate)){
			alert(setPopMessage("1503"));  //[확인] 시작일자가 종료일자보다 클 수는 없습니다.
			setMessage("1503");
			return false;
		}
		if(length(trim(pNo)) > 1 && length(eDate) ==8){
			inputQtyCheck(pNo,varNewValue,eDate);  //당월,전년,금년 입고수량CHECK
		}
	}
	if( strColumnID == "SGEDT" ){
		if (length(varNewValue) < 8 || toNumber(nowDate) < toNumber(varNewValue)) {
			alert(setPopMessage("1505"));  //[확인] 종료일자를 확인바랍니다.
			setMessage("1505");	
			return false;
		}
		var startDate = ds_BIDC251.GetColumn(nRow,"SGSDT");
		if(length(startDate) == 8){
			if(toNumber(startDate) > toNumber(varNewValue)){
				alert(setPopMessage("1505"));  //[확인] 종료일자를 확인바랍니다.
				setMessage("1505");	
				return false;
			}
		}
		var pNo = ds_BIDC251.GetColumn(nRow,"SGPNO");
		var sDate = ds_BIDC251.GetColumn(nRow,"SGSDT");
		if(length(sDate) ==8 && toNumber(sDate) > toNumber(varNewValue)){
			alert(setPopMessage("1503"));  //[확인] 시작일자가 종료일자보다 클 수는 없습니다.
			setMessage("1503");
			return false;
		}
		if(length(trim(pNo)) > 1 && length(sDate) ==8){
			inputQtyCheck(pNo,sDate,varNewValue);  //당월,전년,금년 입고수량CHECK
		}
	}
	if( strColumnID == "SGRSN" ){
		if (length(varNewValue) < 1) {
			alert(setPopMessage("5005"));  //[확인] 원인코드를 확인바랍니다.
			setMessage("5005");	
			return false;
		}
		//원인코드Check
		http.Sync = true;
		var sKind		= "getBUY050PR";		
		var sMethodName = "service::pscomlib/selectBUY050PR.do"; 
		var sInDataSet  = "";
		var sOutDataSet = "ds_BUY050PR=ds_BUY050PR";
		var sArgument   = "";
			sArgument  += " LIB2=" + quote(Lib2);
			sArgument  += " BUYCO=" + quote(cb_searchCodeName1.Value);
			sArgument  += " BUYCD=" + quote("BID"+varNewValue);
		Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
		if (ds_BUY050PR.GetRowCount() < 1){
			alert(setPopMessage("5005"));  // [확인] 원인코드를 확인바랍니다.
			setMessage("5005");	
			return false;
		}
		/*
		var pNo = ds_BIDC251.GetColumn(nRow,"SGPNO");
		var sDate = ds_BIDC251.GetColumn(nRow,"SGSDT");
		var eDate = ds_BIDC251.GetColumn(nRow,"SGEDT");
		if(length(trim(pNo)) > 1 && length(sDate) ==8 && length(eDate) ==8){
			inputQtyCheck(nRow);  //당월,전년,금년 입고수량CHECK
		}
		*/
	}
	http.usewaitcursor = true;
}

function ds_BIDC251_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if (strColumnID == "SGPNO"){
		ds_BIDC251.SetColumn(nRow,"ITDSC",can_ITDSC);  // default값 설정
		ds_BIDC251.SetColumn(nRow,"SGITC",can_ITCLS);  // default값 설정
		var sDate = ds_BIDC251.GetColumn(nRow,"SGSDT");
		var eDate = ds_BIDC251.GetColumn(nRow,"SGEDT");
		if(length(sDate) == 8 && length(eDate) ==8){
			ds_BIDC251.SetColumn(nRow,"RPQTM",can_MQY);  // default값 설정
			ds_BIDC251.SetColumn(nRow,"RPQTA",can_AQY);  // default값 설정
			ds_BIDC251.SetColumn(nRow,"RPQTB",can_BQY);  // default값 설정
			ds_BIDC251.SetColumn(nRow,"RPQTY",toNumber(can_BQY)+toNumber(can_AQY)+toNumber(can_MQY));  // default값 설정
		}
	}
	if( strColumnID == "SGSDT" ){
		var pNo = ds_BIDC251.GetColumn(nRow,"SGPNO");
		var eDate = ds_BIDC251.GetColumn(nRow,"SGEDT");
		if(length(trim(pNo)) > 1 && length(eDate) ==8){
			ds_BIDC251.SetColumn(nRow,"RPQTM",can_MQY);  // default값 설정
			ds_BIDC251.SetColumn(nRow,"RPQTA",can_AQY);  // default값 설정
			ds_BIDC251.SetColumn(nRow,"RPQTB",can_BQY);  // default값 설정
			ds_BIDC251.SetColumn(nRow,"RPQTY",toNumber(can_BQY)+toNumber(can_AQY)+toNumber(can_MQY));  // default값 설정
		}
	}
	if( strColumnID == "SGEDT" ){
		var pNo = ds_BIDC251.GetColumn(nRow,"SGPNO");
		var sDate = ds_BIDC251.GetColumn(nRow,"SGSDT");
		if(length(trim(pNo)) > 1 && length(sDate) ==8){
			ds_BIDC251.SetColumn(nRow,"RPQTM",can_MQY);  // default값 설정
			ds_BIDC251.SetColumn(nRow,"RPQTA",can_AQY);  // default값 설정
			ds_BIDC251.SetColumn(nRow,"RPQTB",can_BQY);  // default값 설정
			ds_BIDC251.SetColumn(nRow,"RPQTY",toNumber(can_BQY)+toNumber(can_AQY)+toNumber(can_MQY));  // default값 설정
		}
	}
	/*
