function inputQtyCheck(p_pspno,sta_TRDT,end_TRDT){
	/*
	var sta_TRDT = ds_BIDC251.GetColumn(nRow,"SGSDT");
	var end_TRDT = ds_BIDC251.GetColumn(nRow,"SGEDT");
	var sta_TRYM = substr(ds_BIDC251.GetColumn(nRow,"SGSDT"),0,6);
	var end_TRYM = substr(ds_BIDC251.GetColumn(nRow,"SGEDT"),0,6);
	var sta_TRYY = substr(ds_BIDC251.GetColumn(nRow,"SGSDT"),0,4);
	var end_TRYY = substr(ds_BIDC251.GetColumn(nRow,"SGEDT"),0,4);
	var sta_TRMM = substr(ds_BIDC251.GetColumn(nRow,"SGSDT"),4,2);
	var end_TRMM = substr(ds_BIDC251.GetColumn(nRow,"SGEDT"),4,2);
	var sta_TRDD = substr(ds_BIDC251.GetColumn(nRow,"SGSDT"),6,2);
	var end_TRDD = substr(ds_BIDC251.GetColumn(nRow,"SGEDT"),6,2);
	*/
	var sta_TRYM = substr(sta_TRDT,0,6);
	var end_TRYM = substr(end_TRDT,0,6);
	var sta_TRYY = substr(sta_TRDT,0,4);
	var end_TRYY = substr(end_TRDT,0,4);
	var sta_TRMM = substr(sta_TRDT,4,2);
	var end_TRMM = substr(end_TRDT,4,2);
	var sta_TRDD = substr(sta_TRDT,6,2);
	var end_TRDD = substr(end_TRDT,6,2);
	var RPQTM_dt1,RPQTM_dt2;  //당월입고수량 시작일자,종료일자
	if(sta_TRYM == end_TRYM){
		RPQTM_dt1 = sta_TRDT;
		RPQTM_dt2 = end_TRDT;
	}else{
		RPQTM_dt1 = end_TRYM + "01";
		RPQTM_dt2 = end_TRDT;
	}
	var RPQTA_dt1,RPQTA_dt2; //금년입고수량 시작일자,종료일자
	if(sta_TRYM == end_TRYM){
		RPQTA_dt1 = "0";
		RPQTA_dt2 = "0";
	}else{
		RPQTA_dt1 = iif(toNumber(end_TRMM) < 4, toNumber(end_TRYY)-1 + "0401",end_TRYY+"0401");
		RPQTA_dt2 = AddDate(end_TRYM + "01",-1);
	}
	var RPQTB_dt1,RPQTB_dt2; //전년입고수량 시작일자,종료일자
	if(sta_TRYM == end_TRYM){
		RPQTB_dt1 = "0";
		RPQTB_dt2 = "0";
	}else{
		RPQTB_dt1 = sta_TRDT;
		RPQTB_dt2 = AddDate(RPQTA_dt1,-1);
	}
	//var p_pspno = ds_BIDC251.GetColumn(nRow,"SGPNO");
	var sKind		= "getBIDC251_BID100";		
	var sMethodName = "service::psimmlib/selectBIDC251_BID100.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_BIDC251_BID100=ds_BIDC251_BID100";
	var sArgument   = "";
		sArgument  += " LIB2=" + quote(Lib2);
		sArgument  += " COMPS=" + quote(cb_searchCodeName1.Value);
		sArgument  += " VNDNR=" + quote(trim(ed_SGBVN.Text));
		sArgument  += " PSPNO=" + quote(p_pspno);
		sArgument  += " TRNDT1=" + quote(sta_TRDT);
		sArgument  += " TRNDT2=" + quote(end_TRDT);
		sArgument  += " TRNDTM1=" + quote(RPQTM_dt1);
		sArgument  += " TRNDTM2=" + quote(RPQTM_dt2);
		sArgument  += " TRNDTA1=" + quote(RPQTA_dt1);
		sArgument  += " TRNDTA2=" + quote(RPQTA_dt2);
		sArgument  += " TRNDTB1=" + quote(RPQTB_dt1);
		sArgument  += " TRNDTB2=" + quote(RPQTB_dt2);
	http.Sync = true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync = false;
	if (ds_BIDC251_BID100.GetRowCount() > 0){
		can_MQY = ds_BIDC251_BID100.GetColumn(0,"MQY");
		can_AQY = ds_BIDC251_BID100.GetColumn(0,"AQY");
		can_BQY = ds_BIDC251_BID100.GetColumn(0,"BQY");
	}
}
function ed_Action_OnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam)
{
	if(nChar == 13){
		switch(obj.text){
			case "A" : fn_Input(); break;
			case "Q" : fn_Search(); break;
			default : alert(setPopMessage("1016")); setMessage("1016");
		}
	}
}

function MainGrid_OnKeyDown(obj,nChar,bShift,bCtrl,bAlt,nLLParam,nHLParam)
{
	if(nChar == 13){
		if(confirm("저장하시겠습니까? ") == false) return;
		fn_Save();
	}
}

function ed_SGBVN_OnKillFocus(obj)
{
	if(length(trim(obj.text)) > 0){
		http.Sync 	= true;
		var sKind		= "getVENNAM";		
		var sMethodName = "service::pscomlib/selectVENNAM.do"; 
		var sInDataSet  = "";
		var sOutDataSet = "ds_VENNAM=ds_VENNAM";
		var sArgument   = "";
			sArgument  += " LIB1=" + quote(Lib1);
			sArgument  += " VNDNR=" + quote(trim(ed_SGBVN.Text));
		Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
		if (ds_VENNAM.GetRowCount() != 1){
			alert(setPopMessage("4674"));
			setMessage("4674");	
			st_VNAME.Text = "";
			ed_SGBVN.SetFocus();
			return;
		}
		st_VNAME.Text = ds_VENNAM.GetColumn(0,"VNAME");
	}else{
		st_VNAME.Text = "";
	}
}
