}

function getComCode(){
	http.Sync 	= true;
	var sKind		= "getComCode";						
	var sMethodName = "service::login/selectComCode.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ComCode_PLNT=ds_ComCode_PLNT";
	var sArgument   = "";
		sArgument   += " CLASS='PLNT'";
		sArgument   += " CODE1=" + quote(company);		
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	ComboBoxPlant.Value = comps;
}

/**********************************************************************
 * 내용설명 : 단가소급처리 대상품번 전표번호 조회(MST040PF)
 **********************************************************************/
function getMST040PF(){
	http.Sync 	= true;
	var sKind		= "getMST040PF";						
	var sMethodName = "service::pscomlib/selectMST040PF.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_MST040PF=ds_MST040PF";
	var sArgument   = "";
		sArgument += " LIB2=" + quote(Lib2);
		sArgument += " COMPS=" + quote(ComboBoxPlant.Value);
		sArgument += " MAJCD=" + quote("BIDC251");		
	http.Sync = true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync = false;
}
/**********************************************************************
 * 내용설명 : 단가소급처리 대상품번 전표번호 저장(MST040PF)
 **********************************************************************/
 function setMst040pf()
 {

	http.Sync = true;
	var sKind		= "setmst040";
	var sMethodName = "service::pscomlib/updateMST040.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "";    		 								// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
	sArgument += " LIB2=" + quote(Lib2);
	sArgument += " COMPS=" + quote(ComboBoxPlant.Value);
	sArgument += " MAJCD=" + quote("BIDC251");
	sArgument += " MJINT=" + quote(st_ASGBJP.Text);
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");

}
/********************************************************************** 
 * 내용설명 : 화면조회
 * 주요변수 : 
 **********************************************************************/ 
function getBIDC251UC(){
	//ds_BIDC251UC.ClearData();
	ComboBoxPlant_OnChanged();

	FileDialog0.Filter = "ALL File(.xls;.xlsx)|*.xls;*.xlsx";	
	FileDialog0.Type = "OPEN";
	if (!FileDialog0.Open()) {
		alert(setPopMessage("1009"));	//  파일 선택 창을 여는데 실패하였습니다.
		return;
	} 

	Div2.Visible = true;
	file_name = FileDialog0.FileName;

	var file_path = FileDialog0.FilePath;
	var path = file_path + "\\" + file_name;

	setMessageDirect(path);	

	ext_ExcelImportByIndex(path,0,"ds_BIDC251UC",1,1,0,2);//upload1

	if (ds_BIDC251UC.rowcount < 1){
		alert("[경고] 생성할 data가 없습니다. 확인바랍니다."); 
		return;	
	}
	//마지막전표번호가져오기
	getMST040PF();
	if (ds_MST040PF.rowcount == 0){
		alert(setPopMessage("1444"));  // [경고] 시스템에 문제가 발생하였습니다. 디오네 종료 후 재가동바랍니다.
		setMessage("1444");	
		return;
	}
	st_BSGBJP.Text = right("0000000"+ds_MST040PF.GetColumn(0,"MJINT"),7);	//전번호
	ed_P_SGBJP.Text= trim(ds_MST040PF.GetColumn(0,"MJCHR"));	//전표구분코드
	ed_SGBJP.Text = toNumber(ds_MST040PF.GetColumn(0,"MJINT"))+1;	//시작번호
	//END
	//ds_BIDC251UC.Sort("COMPS,SGVND,SGPNO,SGSDT,SGEDT,SGITC"); //사업부,협력업체,품번,시작일자.종료일자,제품
	ds_BIDC251UC.Sort("COMPS,SGVND,SGSDT,SGEDT,SGPNO,SGITC"); //사업부,협력업체,시작일자.종료일자,품번,제품

	var p_SGJNO="";

	var p_SGVND="";
	var p_SGPNO="";
	var p_SGSDT="";
	var p_SGEDT="";
	var p_SGITC="";

	var p_ChkData="";
	var p_SGNOSeq=0;
	var p_Cnt=0;
	//전표번호는 1.동일한 업체, 2.시작과종료일자가 같은항목의 최대15개 등록
	//한전표에 동일한 품번은 존재할수 없음.
	for(i=0; i<ds_BIDC251UC.rowcount; i++){
		// Start(validation 체크)
		var validation =  validationCheck(i);
		//trace("validation = "+validation);
		if (validation = false){
			c_valChk="NG";
			break;
		}else{
			c_valChk="OK";
		}
		// End

		// Start(전표번호부여)
		p_Cnt++;
		if(i=0){
			p_SGNOSeq = right("0000000"+ed_SGBJP.Text,7);
			p_SGJNO =ed_P_SGBJP.Text + p_SGNOSeq;
			ds_BIDC251UC.SetColumn(i,"SGJNO",p_SGJNO);
		}else{
			//동일한 품번이 있는경우 신규전표번호생성
			if(p_SGVND != trim(ds_BIDC251UC.GetColumn(i,"SGVND")) ||
				p_SGSDT != trim(ds_BIDC251UC.GetColumn(i,"SGSDT")) ||
				p_SGEDT != trim(ds_BIDC251UC.GetColumn(i,"SGEDT")) ||
				p_SGPNO == trim(ds_BIDC251UC.GetColumn(i,"SGPNO"))){
					p_SGNOSeq = toNumber(p_SGNOSeq)+1;
					p_SGJNO = ed_P_SGBJP.Text + right("0000000"+p_SGNOSeq,7);
					ds_BIDC251UC.SetColumn(i,"SGJNO",p_SGJNO);
					p_Cnt =0;
			}else{
				//trace(p_Cnt);
				if(p_Cnt > 5){
					p_SGNOSeq = toNumber(p_SGNOSeq)+1;
					p_SGJNO =ed_P_SGBJP.Text + right("0000000"+p_SGNOSeq,7);
					p_Cnt =0;
				}
				ds_BIDC251UC.SetColumn(i,"SGJNO",p_SGJNO);
			}
			/*
			//동일한 품번이 있는경우 에러발생
			if(p_SGVND != trim(ds_BIDC251UC.GetColumn(i,"SGVND")) ||
				p_SGSDT != trim(ds_BIDC251UC.GetColumn(i,"SGSDT")) ||
				p_SGEDT != trim(ds_BIDC251UC.GetColumn(i,"SGEDT"))){
					p_SGNOSeq = toNumber(p_SGNOSeq)+1;
					p_SGJNO = ed_P_SGBJP.Text + right("0000000"+p_SGNOSeq,7);
					ds_BIDC251UC.SetColumn(i,"SGJNO",p_SGJNO);
					p_Cnt =0;
			}else{
				if(p_SGPNO == trim(ds_BIDC251UC.GetColumn(i,"SGPNO"))){
					alert("[알림]업체, 품번, 시작일자, 종료일자가 동일한 품번이 존재합니다.");
					break;
				}
				//trace(p_Cnt);
				if(p_Cnt > 5){
					p_SGNOSeq = toNumber(p_SGNOSeq)+1;
					p_SGJNO =ed_P_SGBJP.Text + right("0000000"+p_SGNOSeq,7);
					p_Cnt =0;
				}
				ds_BIDC251UC.SetColumn(i,"SGJNO",p_SGJNO);
			}
			*/
		}
		p_SGVND = trim(ds_BIDC251UC.GetColumn(i,"SGVND"));
		p_SGSDT = trim(ds_BIDC251UC.GetColumn(i,"SGSDT"));
		p_SGEDT = trim(ds_BIDC251UC.GetColumn(i,"SGEDT"));
		p_SGPNO = trim(ds_BIDC251UC.GetColumn(i,"SGPNO"));
		//fn_Input();
		// End
	}
	st_ASGBJP.Text = p_SGNOSeq;	//종료번호
	//st_ASGBJP.Text = right("0000000"+p_SGNOSeq,7);	//종료번호
	Div2.Visible = false;
}

/********************************************************************** 
 * 내용설명 : 저장
 * 주요변수 : 
 **********************************************************************/ 
 function setBIDC251UC(){

	if (c_valChk != "OK"){
		alert("[알림]DATA검증 완료 후 저장하시기 바랍니다.");
		return;
	}
	var sKind		= "setBIDC251UC";						
	var sMethodName = "service::psimmlib/saveBIDC251UC.do";
	var sInDataSet  = "ds_BIDC251UC=ds_BIDC251UC:U";
	var sOutDataSet = "ds_BIDC251UC_CHECK=ds_BIDC251_CHECK";
	var sArgument   = "";
	sArgument += " LIB2=" + quote(Lib2);
	sArgument += " ADDID=" + quote(G_ds_user.GetColumn(0,"USER_ID"));
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");

 }

