}


/********************************************************************** 
 * 내용설명 : Transaction 리턴후 실행 함수
 * 주요변수 : 
 * 			- sMethodName	Transaction 고유 ID
 * 			- ErrorCode 	에러코드
 * 			- ErrorMSG 		에러 메세지
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	if (svcId = "setBIDC251UC"){
		if (ds_BIDC251UC_CHECK.rowcount > 0){			
			for(var i=0; i<ds_BIDC251UC_CHECK.rowcount; i++){		
				alert("전표번호 : " +ds_BIDC251UC_CHECK.GetColumn(i,"SGJNO") + " , "
				    + "업체 : " +ds_BIDC251UC_CHECK.GetColumn(i,"SGVND") + " , "
				    + "품번 : " + ds_BIDC251UC_CHECK.GetColumn(i,"SGPNO") + " ="
					+ ds_BIDC251UC_CHECK.GetColumn(i,"RTMSG"));	
			}
		//return;		
		}
	}
	// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");
	}else {
		alert(setPopMessage("1001"));
		setMessage("1001");	
	}
}

