//DATA검증
function validationCheck(i){

	var c_COMPS=trim(ds_BIDC251UC.GetColumn(i,"COMPS"));	//사업부코드

	var c_SGVND=trim(ds_BIDC251UC.GetColumn(i,"SGVND"));	//협력업체
	var c_SGPNO=trim(ds_BIDC251UC.GetColumn(i,"SGPNO"));	//품번
	//var c_SGITC=trim(ds_BIDC251UC.GetColumn(i,"SGITC"));	//제품
	var c_SGRSN=trim(ds_BIDC251UC.GetColumn(i,"SGRSN"));	//원인코드
	var c_SGSDT=trim(ds_BIDC251UC.GetColumn(i,"SGSDT"));	//시작일자
	var c_SGEDT=trim(ds_BIDC251UC.GetColumn(i,"SGEDT"));	//종료일자

	//var c_RPQTM=trim(ds_BIDC251UC.GetColumn(i,"RPQTM"));	//당월수량
	//var c_RPQTA=trim(ds_BIDC251UC.GetColumn(i,"RPQTA"));	//금년도수량
	//var c_RPQTB=trim(ds_BIDC251UC.GetColumn(i,"RPQTB"));	//전년도수량
	//var c_RPQTY=trim(ds_BIDC251UC.GetColumn(i,"RPQTY"));	//수량합

	//사업부 코드체크
	if(ComboBoxPlant.Value != c_COMPS){
		alert("[경고] 사업부 구분을 확인바랍니다.");
		//alert(setPopMessage("1510"));  //[경고] 사업부 구분을 확인바랍니다.
		//setMessage("1510");
		return false;
	}
	//협력업체 체크
	if(length(c_SGVND) < 1){
		alert("[알림]업체를 입력하세요.");
		alert(setPopMessage("4674"));  //[알림]업체를 입력하세요.
		setMessage("4674");
		return false;
	}else{
	/*
		//개별조회
		var valCheck =  getVENNAM(c_SGVND);
		if (valCheck = false) {
			alert("[확인] 납품업체를 확인바랍니다.");
			//alert(setPopMessage("4007"));  // [확인] 납품업체를 확인바랍니다.
			//setMessage("4007");
			return false;
		}
		//or  == >SQL 수정 : TRIM(VNDNR) A1, VNAME A2, VADD1 A3
	*/
		//PGM로딩시 dataset에 코드 저장 후 체크
		if(ds_VENNAM.FindRow("A1", c_SGVND) == -1){
			alert("[확인] 납품업체를 확인바랍니다.");
			//alert(setPopMessage("4007"));  // [확인] 납품업체를 확인바랍니다.
			//setMessage("4007");
			return false;
		}

	}
	//Item Master체크(품번입력여부)
	if(length(c_SGPNO) < 1){
		alert("[경고] 필수입력항목입니다. (품번)");
		//alert(setPopMessage("2501"));  //[경고] 필수입력항목입니다. (품번)
		//setMessage("2501");	
		return false;
	}else{
		var valCheck =  getBM008PR(c_SGPNO);
		//trace("pspno"+valCheck);
		if (valCheck = false) {
			alert("[경고] Item Master에 미등록된 품번입니다.");
			//alert(setPopMessage("1509"));  // [경고] Item Master에 미등록된 품번입니다.
			//setMessage("1509");
			return false;
		}
		ds_BIDC251UC.SetColumn(i,"ITDSC",ds_BM008PR.GetColumn(0,"ITDSC"));
		/*
		if(c_SGITC != trim(substr(ds_BM008PR.GetColumn(0,"ITCLS"),0,1))){
			alert("[경고] Item Master에 등록된 제품코드와 다릅니다.");
			//alert(setPopMessage("1509"));  // [경고] Item Master에 미등록된 품번입니다.
			//setMessage("1509");
			return false;
		}
		*/
		ds_BIDC251UC.SetColumn(i,"SGITC",substr(ds_BM008PR.GetColumn(0,"ITCLS"),0,1));
		//Purchase Price Master 체크
		var valCheck =  getCM001PR(c_SGPNO, c_SGVND);
		if (valCheck = false) {
			alert("[경고] Purchase Price Master에 미등록된 품번입니다.");
			//alert(setPopMessage("1511"));  // [경고] Purchase Price Master에 미등록된 품번입니다.
			//setMessage("1511");	
			return false;
		}else{	//일자체크
			if (length(c_SGSDT) != 8){
				alert("[확인] 시작일자를 확인바랍니다.");
				//alert(setPopMessage("1504"));  //[확인] 시작일자를 확인바랍니다.
				//setMessage("1504");	
				return false;
			}else if (length(c_SGEDT) != 8){
				alert("[확인] 종료일자를 확인바랍니다.");
				//alert(setPopMessage("1505"));  //[확인] 종료일자를 확인바랍니다.
				//setMessage("1505");	
				return false;
			}else if (toNumber(c_SGSDT) > toNumber(c_SGEDT)){
				alert("[확인] 시작일자가 종료일자보다 클 수는 없습니다.");
				//alert(setPopMessage("1503"));  //[확인] 시작일자가 종료일자보다 클 수는 없습니다.
				//setMessage("1503");
				return false;
			}
			//당월,전년,금년 입고수량CHECK
			if(length(c_SGSDT) == 8 && length(c_SGEDT) ==8){
				var valCheck = inputQtyCheck(c_SGVND, c_SGPNO,c_SGSDT,c_SGEDT);  //당월,전년,금년 입고수량CHECK
				if (valCheck = false) {
					alert("[경고]자재입고마스타에 미등록된 품번입니다.");
					//alert(setPopMessage("1511"));  // [경고] Purchase Price Master에 미등록된 품번입니다.
					//setMessage("1511");	
					return false;
				}
				else{
					var c_MQY=ds_BIDC251UC_BID100.GetColumn(0,"MQY"); //자재입고마스타 당월수량
					var c_AQY=ds_BIDC251UC_BID100.GetColumn(0,"AQY"); //자재입고마스타 금년도수량
					var c_BQY=ds_BIDC251UC_BID100.GetColumn(0,"BQY"); //자재입고마스타 전년도수량
					/*
					if (c_RPQTM != toNumber(c_MQY)){
						alert("[확인] 자재입고마스타에 등록된 당월수량과 업로드 당월수량이 다릅니다.");
						return false;
					}
					if (c_RPQTA != toNumber(c_AQY)){
						alert("[확인] 자재입고마스타에 등록된 금년도수량과 업로드 금년도수량이 다릅니다.");
						return false;
					}
					if (c_RPQTB != toNumber(c_BQY)){
						alert("[확인] 자재입고마스타에 등록된 \n 전년도수량과 업로드 전년도수량이 다릅니다.");
						return false;
					}
					if (c_RPQTY != (toNumber(c_MQY)+ toNumber(c_AQY)+ toNumber(c_BQY))){
						alert("[확인] 총합계 수량이 다릅니다.");
						return false;
					}
					*/
					ds_BIDC251UC.SetColumn(i,"RPQTM",c_MQY);
					ds_BIDC251UC.SetColumn(i,"RPQTA",c_AQY);
					ds_BIDC251UC.SetColumn(i,"RPQTB",c_BQY);
					ds_BIDC251UC.SetColumn(i,"RPQTY",(toNumber(c_MQY)+ toNumber(c_AQY)+ toNumber(c_BQY)));
				}	
			}
		}
	}
	//원인코드Check 체크
	/*
	//개별조회
	var valCheck =  getBUY050PR("BID"+c_SGRSN);
	if (valCheck = false) {
		alert("[확인] 원인코드를 확인바랍니다.");
		//alert(setPopMessage("5005"));  // [확인] 원인코드를 확인바랍니다.
		//setMessage("5005");	
		return false;
	}
	//or SQL수정
	*/
	//PGM로딩시 dataset에 코드 저장 후 체크
	if(ds_BUY050PR.FindRow("BUYCD", "BID"+c_SGRSN) == -1){
		alert("[확인] 원인코드를 확인바랍니다.");
		//alert(setPopMessage("5005"));  // [확인] 원인코드를 확인바랍니다.
		//setMessage("5005");	
		return false;
	}

	return true;
}
