//당월,전년,금년 입고수량CHECK
function inputQtyCheck(p_SGVND, p_pspno,sta_TRDT,end_TRDT){

	var sta_TRYM = substr(sta_TRDT,0,6);
	var end_TRYM = substr(end_TRDT,0,6);
	var sta_TRYY = substr(sta_TRDT,0,4);
	var end_TRYY = substr(end_TRDT,0,4);
	var sta_TRMM = substr(sta_TRDT,4,2);
	var end_TRMM = substr(end_TRDT,4,2);
	var sta_TRDD = substr(sta_TRDT,6,2);
	var end_TRDD = substr(end_TRDT,6,2);
	var RPQTM_dt1,RPQTM_dt2;  //당월입고수량 시작일자,종료일자
	if(sta_TRYM == end_TRYM){
		RPQTM_dt1 = sta_TRDT;
		RPQTM_dt2 = end_TRDT;
	}else{
		RPQTM_dt1 = end_TRYM + "01";
		RPQTM_dt2 = end_TRDT;
	}
	var RPQTA_dt1,RPQTA_dt2; //금년입고수량 시작일자,종료일자
	if(sta_TRYM == end_TRYM){
		RPQTA_dt1 = "0";
		RPQTA_dt2 = "0";
	}else{
		RPQTA_dt1 = iif(toNumber(end_TRMM) < 4, toNumber(end_TRYY)-1 + "0401",end_TRYY+"0401");
		RPQTA_dt2 = AddDate(end_TRYM + "01",-1);
	}
	var RPQTB_dt1,RPQTB_dt2; //전년입고수량 시작일자,종료일자
	if(sta_TRYM == end_TRYM){
		RPQTB_dt1 = "0";
		RPQTB_dt2 = "0";
	}else{
		RPQTB_dt1 = sta_TRDT;
		RPQTB_dt2 = AddDate(RPQTA_dt1,-1);
	}
	//var p_pspno = ds_BIDC251UC.GetColumn(nRow,"SGPNO");
	var sKind		= "getBIDC251_BID100";		
	var sMethodName = "service::psimmlib/selectBIDC251_BID100.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_BIDC251UC_BID100=ds_BIDC251_BID100";
	var sArgument   = "";
		sArgument  += " LIB2=" + quote(Lib2);
		sArgument  += " COMPS=" + quote(ComboBoxPlant.Value);
		sArgument  += " VNDNR=" + quote(p_SGVND);
		sArgument  += " PSPNO=" + quote(p_pspno);
		sArgument  += " TRNDT1=" + quote(sta_TRDT);
		sArgument  += " TRNDT2=" + quote(end_TRDT);
		sArgument  += " TRNDTM1=" + quote(RPQTM_dt1);
		sArgument  += " TRNDTM2=" + quote(RPQTM_dt2);
		sArgument  += " TRNDTA1=" + quote(RPQTA_dt1);
		sArgument  += " TRNDTA2=" + quote(RPQTA_dt2);
		sArgument  += " TRNDTB1=" + quote(RPQTB_dt1);
		sArgument  += " TRNDTB2=" + quote(RPQTB_dt2);
	http.Sync = true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync = false;
	if (ds_BIDC251UC_BID100.rowcount < 1) return false;
}

function fn_total()
{
	var total;
	for(var i=0;i<ds_BIDC251UC.GetRowCount();i++){
		//(toNumber(SGCST)-toNumber(RPCST))*toNumber(RPQTY)
		var column1 = toNumber(ds_BIDC251UC.GetColumn(i,"SGCST"));
		var column2 = toNumber(ds_BIDC251UC.GetColumn(i,"RPCST"));
		var column3 = toNumber(ds_BIDC251UC.GetColumn(i,"RPQTY"));
		total += (column1-column2)*column3;
	}
	return total;
}

//양식다운로드
function getFormDown(obj)
{
	//양식경로
	var gImgPath = "form/";
	//다운로드 받을 파일명을 취득하여 변수에 설정(클릭한 로우의 파일)
	var str_param ="단가소급_업로드양식.xlsx";
	//다운로드 받을 파일이 저장되어있는 서버 경로와 파일명
	var str_param_server = imgUPDOWNpath + gImgPath+str_param;			
	//-----------  다이얼로그창  설정------------------
    //다이얼로그창에 다운받을 파일명 설정
	FileDialog0.FileName= str_param;
    //다이얼로그창에 다운받을 기본경로 설정
	FileDialog0.FilePath= "C:\\";
	//다이얼로그창 버튼 SAVE로 설정
	FileDialog0.Type = "SAVE";
	//확장자가 없을 때 기본 확장자 설정
	FileDialog0.AppendExtDefault = "TRUE";
	//다이얼로그 생성(확인 버튼 클릭시 true, 취소버튼을 클릭시 false 반환)
	//확인 버튼 클릭시만 처리 계속
	if(FileDialog0.Open()){
		//다운로드 받을 경로(로컬)
		var str_param_full = FileDialog0.FilePath + "\\" + FileDialog0.FileName;		
		//trace("파일명:" + str_param);
		//trace("경로(파일명):" + str_param_full);

		//스크립트 파일의 함수 호출 
		var file_size = CyHttpFile0.GetFileSizeLocal(str_param_server);
		//trace("파일사이즈:" + file_size);
		var ret = gfn_FileRead(CyHttpFile0, str_param_full,str_param_server, file_size, 1, "prog_bar", "");

		//호출함수 리턴값 처리
		if (ret[0] == "FAIL"){
			alert(ret[1]);
		}else{
			alert("[알림]양식이 정상적으로 PC에 저장되었습니다.");
		}
	}
}

