/********************************************************************** 
 * 내용설명 : 사업부 선택
 * 주요변수 : 
 **********************************************************************/ 
function getComCode(){

	http.Sync 	= true;

	var sKind		= "getComCode";						
	var sMethodName = "service::login/selectComCode.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ComCode_PLNT=ds_ComCode_PLNT";
	var sArgument   = "";
		sArgument   += " CLASS='PLNT'";
		sArgument   += " CODE1=" + quote(company);		
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	ComboBoxPlant.Value = comps;
}


/********************************************************************** 
 * 내용설명 : 화면조회
 * 주요변수 : 
 **********************************************************************/ 
function getBIDZ211Q(){
	if(length(ca_MAGYM.Value) < 6){
		alert(setPopMessage("4020"));
		setMessage("4020");
		return;
	}
	if(!(ed_GUBUN.Text == "A" || ed_GUBUN.Text == "B" || ed_GUBUN.Text == "C")){
		alert(setPopMessage("5001")); //[확인] 마감구분을 확인바랍니다.
		setMessage("5001");
		return;
	}
	/* 2018.04.19 삭제
	var JDELib4;
	if (Lib2 == "PSEDBLIB"){
		JDELib4 = "CLTDTAE";
	}else{
		JDELib4 = "CLTDTAS";
	}
	*/
	var fromdt,todt;
	switch(ed_GUBUN.Text) {
		case "A":
			fromdt = substr(ca_MAGYM.Value,0,6)+"01";
			todt = substr(ca_MAGYM.Value,0,6)+"15";
			break;
		case "B":
			fromdt = substr(ca_MAGYM.Value,0,6)+"01";
			todt = substr(ca_MAGYM.Value,0,6)+"31";
			break;
		case "C":
			fromdt = substr(ca_MAGYM.Value,0,6)+"01";
			todt = substr(ca_MAGYM.Value,0,6)+"31";
			break;
	}

	var p_vnd1,p_vnd2;
	p_vnd1 = iif (length(trim(ed_CUVND1.Text)) == 0, ' ', trim(ed_CUVND1.Text));
	p_vnd2 = iif (length(trim(ed_CUVND2.Text)) == 0 , '999999', trim(ed_CUVND2.Text));

	var sKind		= "getBIDZ211Q";						
	var sMethodName = "service::psimmlib/selectBIDZ211Q.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_BIDZ211Q=ds_BIDZ211Q";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";

	sArgument += "@LIB1=" + quote(Lib1);
	sArgument += "@LIB2=" + quote(Lib2);
	//sArgument += "@LIB4=" + quote(JDELib4);
	sArgument += "@LIB4=" + quote(Lib4);
	sArgument += "@COMPS=" + quote(ComboBoxPlant.Value);
	sArgument += "@CUVND1=" + quote(p_vnd1);
	sArgument += "@CUVND2=" + quote(p_vnd2);
	sArgument += "@TRNDT1=" + quote(fromdt);
	sArgument += "@TRNDT2=" + quote(todt);
	sArgument += "@QGUB=" + quote(rdoggub.Value);
	sArgument += "@APART=" + quote(cmb_APART.Value);
	sArgument += "@CGUB=" + quote(ed_GUBUN.Text);
	var str = dialog("Common::PGMLOAD_IMM.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");
	if (str == 'T'){  //정상조회
		alert(setPopMessage("1004"));
		setMessage("1004");	
	}else if (str == 'B'){   //data값이 없을때
		alert(setPopMessage("1003"));  //[알림] 조회 된 데이터가 없습니다.
		setMessage("1003");	
	}else if (str == 'E'){   //error발생시
		alert(setPopMessage("1002"));
		setMessage("1002");	
	}
	//Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
}


