/********************************************************************** 
 * 내용설명 : 사업부 선택
 * 주요변수 : 
 **********************************************************************/ 
function getComCode(){

	http.Sync 	= true;

	var sKind		= "getComCode";						
	var sMethodName = "service::login/selectComCode.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ComCode_PLNT=ds_ComCode_PLNT";
	var sArgument   = "";
		sArgument   += " CLASS='PLNT'";
		sArgument   += " CODE1=" + quote(company);		
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	ComboBoxPlant.Value = comps;
}


/********************************************************************** 
 * 내용설명 : 화면조회
 * 주요변수 : 
 **********************************************************************/ 
function getBIDZ212Q(){
	if(length(ca_MAGYM.Value) < 6){
		alert(setPopMessage("4020"));
		setMessage("4020");
		ca_MAGYM.SetFocus();
		return;
	}
	if(!(ed_GUBUN.Text == "A" || ed_GUBUN.Text == "B" || ed_GUBUN.Text == "C")){
		alert(setPopMessage("5001")); //[확인] 마감구분을 확인바랍니다.
		setMessage("5001");
		ed_GUBUN.SetFocus();
		return;
	}
	if(!(ed_APART.Text == "" || ed_APART.Text == "Y")){
		alert(setPopMessage("5001")); //[확인] 마감구분을 확인바랍니다.
		setMessage("5001");
		ed_APART.SetFocus();
		return;
	}
	if(!(ed_MNFCS.Text == "" || ed_MNFCS.Text == "Y")){
		alert(setPopMessage("5001")); //[확인] 마감구분을 확인바랍니다.
		setMessage("5001");
		ed_MNFCS.SetFocus();
		return;
	}
	/* 2018.04.19 삭제
	var JDELib4;
	if (Lib2 == "PSEDBLIB"){
		JDELib4 = "CLTDTAE";
	}else{
		JDELib4 = "CLTDTAS";
	}
	*/

	if(rdoggub.Value = "1" && ed_MNFCS.Text == "Y"){
		MainGrid.SetColProp(16,"Width",100);
		MainGrid.SetColProp(17,"Width",100);
		MainGrid.SetCellProp("Head", 16, "text", "표준단가");
		MainGrid.SetCellProp("Head", 17, "text", "표준금액");
		MainGrid.SetCellProp("body", 16, "align", "right");
		MainGrid.SetCellProp("body", 17, "align", "right");
		MainGrid.SetCellProp("body", 16, "display", "number");
		MainGrid.SetCellProp("body", 17, "display", "number");
		MainGrid.SetCellProp("body", 16, "Mask", "#,##0.###0");
		MainGrid.SetCellProp("body", 17, "Mask", "#,##0.#0");
	}else{
			MainGrid.SetColProp(16,"Width",0);
			MainGrid.SetColProp(17,"Width",0);
	}

	var p_vnd1,p_vnd2;
	p_vnd1 = iif (length(trim(ed_CUVND1.Text)) == 0, ' ', trim(ed_CUVND1.Text));
	p_vnd2 = iif (length(trim(ed_CUVND2.Text)) == 0 , '999999', trim(ed_CUVND2.Text));

	var sKind		= "getBIDZ212Q";						
	var sMethodName = "service::psimmlib/selectBIDZ212Q.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_BIDZ212Q=ds_BIDZ212Q";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";

	sArgument += "@LIB1=" + quote(Lib1);
	sArgument += "@LIB2=" + quote(Lib2);
	//sArgument += "@LIB4=" + quote(JDELib4);
	sArgument += "@LIB4=" + quote(Lib4);
	sArgument += "@COMPS=" + quote(ComboBoxPlant.Value);
	sArgument += "@CUVND1=" + quote(p_vnd1);
	sArgument += "@CUVND2=" + quote(p_vnd2);
	sArgument += "@SFTYM=" + quote(substr(ca_MAGYM.Value,0,6));
	//sArgument += "@TRNDT2=" + quote(todt);
	sArgument += "@QGUB=" + quote(rdoggub.Value);
	sArgument += "@APART=" + quote(ed_APART.Value);
	sArgument += "@CGUB=" + quote(ed_GUBUN.Text); //상,하반기,전체
	sArgument += "@MGUB=" + quote(ed_MNFCS.Text);
	sArgument += "@PFNM=" + quote("F#D30PR");
	var str = dialog("Common::PGMLOAD_IMM.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");	
	//Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
	iif(rdoggub.Value == "1",ds_BIDZ212Q.Sort("CUVND,REFNO,PRTNOB"), ds_BIDZ212Q.Sort("CUVND"));
	if (str == 'T'){  //정상조회
		getSYSDATE(); //SYSTEM 날짜
		alert(setPopMessage("1004"));
		setMessage("1004");	
	}else if (str == 'B'){   //data값이 없을때
		alert(setPopMessage("1003"));  //[알림] 조회 된 데이터가 없습니다.
		setMessage("1003");	
	}else if (str == 'E'){   //error발생시
		alert(setPopMessage("1002"));
		setMessage("1002");	
	}

}


/********************************************************************** 
 * 내용설명 : 엑셀파일로 저장
 * 주요변수 : 
 **********************************************************************/ 
function setExcel()
{
	var fName   = "국내품사입내역서(마감후)";
	var arrSheets  =  Array();  

	arrSheets[0]  = "MainGrid|국내품사입내역서(마감후)";
	createExcelSheetS2(FileDialog0, fName ,arrSheets);

}


/********************************************************************** 
 * 내용설명 : Transaction 리턴후 실행 함수
 * 주요변수 : 
 * 			- sMethodName	Transaction 고유 ID
 * 			- ErrorCode 	에러코드
 * 			- ErrorMSG 		에러 메세지
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");

	}else if(svcId=="getBIDZ212Q"){
		// 조회건수가 없을경우
		if(ds_BIDZ212Q.rowcount <= 0){
			alert(setPopMessage("1003"));
			setMessage("1003");	
		// 정상 조회
		} else {
			alert(setPopMessage("1004"));
			setMessage("1004");				
		}	
	}
}

