function fn_print()
{
	if(ds_BIDZ212Q.rowcount <= 0){
		alert("[알림] 조회후 발행바랍니다.");
		return;
	}

	var sysDt =ds_SYSDATE.GetColumn(0,"DT");
	var systm =ds_SYSDATE.GetColumn(0,"TM");
	var p_vnd1,p_vnd2;
	p_vnd1 = iif (length(trim(ed_CUVND1.Text)) == 0, ' ', trim(ed_CUVND1.Text));
	p_vnd2 = iif (length(trim(ed_CUVND2.Text)) == 0 , '999999', trim(ed_CUVND2.Text));

	ubi0.Visible = true;
	ubi0.setJrfFileDir(G_baseURL+"ui/PSIMMLIB/REPORT/");
	//ubi0.setJrfFileDir("C:/eclipse/workspace/DENSO/WebContent"+"/ui/PSIMMLIB/REPORT/");
	if(rdoggub.Value == 1){
		Ubi0.setJrfFileName("BIDZ212R_D.jrf");
	}else{
		Ubi0.setJrfFileName("BIDZ212R_S.jrf");
	}
	Ubi0.SetDataset("ds_BIDZ212Q", "ds_BIDZ212Q");
	Ubi0.SetArgument("COMPS",ComboBoxPlant.Value);
	Ubi0.SetArgument("VND1",p_vnd1);
	Ubi0.SetArgument("VND2",p_vnd2);
	Ubi0.SetArgument("MGMYM",substr(ca_MAGYM.Value,0,6));
	Ubi0.SetArgument("GUBUN",ed_GUBUN.Text);
	Ubi0.SetArgument("MNFCSGB",iif(length(trim(ed_MNFCS.Text)) == 0,'N',ed_MNFCS.Text));
	Ubi0.SetArgument("APARTGB",iif(length(trim(ed_APART.Text)) == 0,'N',ed_APART.Text));
	Ubi0.SetArgument("SYSDATE",sysDt);
	Ubi0.SetArgument("SYSTIME",systm);	
	Ubi0.Retrieve();

	/*
	sArgument += "@COMPS=" + quote(ComboBoxPlant.Value);
	sArgument += "@CUVND1=" + quote(p_vnd1);
	sArgument += "@CUVND2=" + quote(p_vnd2);
	sArgument += "@SFTYM=" + quote(substr(ca_MAGYM.Value,0,6));
	//sArgument += "@TRNDT2=" + quote(todt);
	sArgument += "@QGUB=" + quote(rdoggub.Value);
	sArgument += "@APART=" + quote(ed_APART.Value);
	sArgument += "@CGUB=" + quote(ed_GUBUN.Text); //상,하반기,전체
	sArgument += "@MGUB=" + quote(ed_MNFCS.Text);
	*/

}

