function getSYSDATE(){
	var sKind		= "getSYSDATE";						
	var sMethodName = "service::pscomlib/selectSYSDATE.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_SYSDATE=ds_SYSDATE";
	var sArgument   = "";
	http.Sync=true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync=false;	
}

