			MainGrid.SetCellProp("body", 6, "display", "normal");
			MainGrid.SetCellProp("body", 7, "display", "normal");
			MainGrid.SetCellProp("body", 8, "display", "normal");
			MainGrid.SetCellProp("body", 9, "display", "normal");
			MainGrid.SetCellProp("body", 10, "display", "number");
			MainGrid.SetCellProp("body", 11, "display", "number");

			MainGrid.SetCellProp("body", 2, "suppress", "0");
			MainGrid.SetCellProp("body", 3, "suppress", "0");

			MainGrid.SetColProp(12,"Width",0);
			MainGrid.SetColProp(13,"Width",0);
			MainGrid.SetColProp(14,"Width",0);
			MainGrid.SetColProp(15,"Width",0);
			MainGrid.SetColProp(16,"Width",0);
			MainGrid.SetColProp(17,"Width",0);
			break;
	}
}

function ed_MNFCS_OnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam)
{
	if(nChar == 13){
		fn_Search();
	}
}
