function getSMDZ122P(){

	if(!validationChk()){
		return;
	}

	getSYSDATE(); //SYSTEM 날짜
	var sysDt =ds_SYSDATE.GetColumn(0,"DT");
	var systm =ds_SYSDATE.GetColumn(0,"TM");

	var sKind		= "getBJIZ108P";						
	var sMethodName = "service::psimmlib/selectBJIZ100.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_BJIZ124P=ds_BJIZ124P";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
	sArgument += "@LIB1=" + quote(Lib1);
	sArgument += "@LIB2=" + quote(Lib2);
	sArgument += "@SQLID=" + "getBjiz100q";  			  		// SQL ID
	sArgument += "@COMPS=" + quote(ComboBoxPlant.Value); 		// 사업부

	sArgument += "@RPIJP_BEFORE=" + quote(ed_RPIJP1.Value); 	// 전표번호 시작
	sArgument += "@RPIJP_AFTER=" + quote(ed_RPIJP2.Value); 		// 전표번호 끝

	sArgument += "@RPIDT_BEFORE=" + quote(cmb_JPYYM1.Value); 	// 입고일자 시작
	sArgument += "@RPIDT_AFTER=" + quote(cmb_JPYYM2.Value); 	// 입고일자 끝

	sArgument += "@RPIVN_ONRY=" + quote(ed_PRDCD1.Value); 	// 업체코드시작	

	if(rdoggub.Value == 'B'){
		sArgument += "@ORDERSTR=" + "RPIJP"; 					// 업체별
	} else {
		sArgument += "@ORDERSTR=" + "RPIVN"; 					// 품번별
	}


	var str = dialog("Common::PGMLOAD_SAL.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");

	if (str == 'T'){  //정상조회	
	}else if (str == 'B'){   //data값이 없을때
		alert(setPopMessage("1003"));
		setMessage("1003");
		return;
	}else if (str == 'E'){   //error발생시
		alert(setPopMessage("1002"));
		setMessage("1002");	
		return;
	}
	var title = substr(cmb_JPYYM1.Value,0,4) + "/" +substr(cmb_JPYYM1.Value,4,2);
	//Ubi0.setJrfFileDir("http://10.74.139.50/ui/PSSALLIB/REPORT/");
	//Ubi0.setJrfFileDir("http://10.72.209.23/ui/PSSALLIB/REPORT/");
	Ubi0.setJrfFileDir(G_baseURL+"ui/PSIMMLIB/REPORT/");

	if(rdoggub.Value == 'B'){
		Ubi0.setJrfFileName("BJIZ108R.jrf");
	} else {
		Ubi0.setJrfFileName("BJIZ109R.jrf");
	}

	Ubi0.SetDataset("ds_BJIZ124P", "ds_BJIZ124P");		
	Ubi0.SetArgument("JPYYM1",title);
	Ubi0.SetArgument("SYSDATE",sysDt);
	Ubi0.SetArgument("SYSTIME",systm);	
	Ubi0.Retrieve();

}

/********************************************************************** 
 * 내용설명 : validation 체크
 **********************************************************************/ 

function validationChk(){
	var result = true;
	if(cmb_JPYYM1.Value == ""){
		Alert("마감년월을 입력 바랍니다.");
		result =false;
	}	
	return result;
}

