}

/********************************************************************** 
 * 내용설명 : 엑셀파일로 저장
 **********************************************************************/ 
function setExcel()
{
	var fName   = "업체별전표별입고현황";
	var arrSheets  =  Array();  

	arrSheets[0]  = "MainGrid|업체별전표별입고현황";
	createExcelSheetS2(FileDialog0, fName ,arrSheets);

}

