var strSeparator = "	";

var Lib1 = G_ds_user.GetColumn(0,"LIB1"); 			// cigma library(pereslib,psreslib)
var Lib2 = G_ds_user.GetColumn(0,"LIB2"); 		// 회사 library(psedblib,pscdblib)
var Lib3 = G_ds_user.GetColumn(0,"LIB3"); 			// 사업부 library(psedblib,pscdblib,pshdblib,pssdblib)
var company = G_ds_user.GetColumn(0,"COMPANY");
var comps = G_ds_user.GetColumn(0,"PLANT_CD");

var nowDate = today();

function on_LoadCompleted(obj){
	getTitle();	
	getDesign();
	getComCode(); 								//공장구분 선택		
	gFcOnTopButtonColor("2.2"); 				//상단 버튼 활성화 여부 (기본:0,조회:1,변경:2)	
	MainGrid.UserData = MainGrid.Height;	
	getMaxBJIZ110(); 							// 초기 전표조회	
}

function getComCode(){
	var sKind		= "getComCode";						
	var sMethodName = "service::login/selectComCode.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ComCode_PLNT=ds_ComCode_PLNT";
	var sArgument   = "";
		sArgument   += " CLASS='PLNT'";
		sArgument   += " CODE1=" + quote(company);		
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	ComboBoxPlant.Value=comps;
}

function getDesign(){
	MainGrid.BkColor = G_ds_design.GetColumn(0,"BkColor");
	MainGrid.BkColor2 = G_ds_design.GetColumn(0,"BkColor2");
	MainGrid.BkSelColor = G_ds_design.GetColumn(0,"BkSelColor");
	MainGrid.Face3dColor = G_ds_design.GetColumn(0,"Face3dColor");
	MainGrid.BoldHead = G_ds_design.GetColumn(0,"BoldHead");
	MainGrid.HeadHeight = G_ds_design.GetColumn(0,"HeadHeight");
	MainGrid.Width = G_ds_design.GetColumn(0,"Width");
	MainGrid.RowHeight = G_ds_design.GetColumn(0,"RowHeight");
	searchBar.Width = G_ds_design.GetColumn(0,"Width");
	searchBar.Top = G_ds_design.GetColumn(0,"SBTop");

	displayDiv.Width = G_ds_design.GetColumn(0,"Width");
}

/********************************************************************** 
 * 내용설명 : 초기 처리로 최초 전표 번호를 수집
 **********************************************************************/ 
function getMaxBJIZ110(){

	var sKind		= "getBJIZ110";						
	var sMethodName = "service::psimmlib/selectBJIZ110.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             					// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_BJIZ110_CHECK=ds_BJIZ110_CHECK";    						// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";

	sArgument += "@LIB2="  + quote(Lib2);
	sArgument += "@COMPS=" + quote(comps);				// 사업부		
	sArgument += "@MAJCD=" + "BJIZ110";  								// BJIZ110 전표 번호 수집 KEY
	sArgument += "@SQLID=" + "getMaxRpijp";  			  				// SQL ID

	var str = dialog("Common::PGMLOAD_IMM.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");

	var mjchr = ds_BJIZ110_CHECK.GetColumn(0,"MJCHR");	
	var mjint = ds_BJIZ110_CHECK.GetColumn(0,"MJINT");	
		st_rpijp.Text = mjint;	             	// 전표번호
		ed_rpijp.Text = (ToInteger(mjint)+1);	// 최종전표번호
		ca_SGBDT.Text = nowDate;	            // 날짜 셋팅   

		//초기화 처리
		ed_SGBVN.Text = "";			             
		st_VNAME.Text = "";
		ds_BJIZ110.ClearData();
}

/********************************************************************** 
 * 내용설명 : 화면 조회 
 **********************************************************************/ 
function getBJIZ110(){

	if(!validationChk()) return;

	var sKind		= "getBJIZ110";						
	var sMethodName = "service::psimmlib/selectBJIZ110.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             					// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_BJIZ110=ds_BJIZ110";    						// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";

	sArgument += "@LIB2=" + quote(Lib2);
	sArgument += "@COMPS=" + quote(ComboBoxPlant.Value);  				// 사업부
	sArgument += "@SQLID=" + "getBJIZ110";  			  				// SQL ID
	sArgument += "@RPIJP=" + "RP-"+ed_rpijp.Value;		  		  		// 전표 번호
	sArgument += "@RPIVN=" + ed_SGBVN.Value;  				  			// 업체코드

	var str = dialog("Common::PGMLOAD_IMM.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");

	if(ds_BJIZ110.count ==0){

		if(st_rpijp.Text >= ed_rpijp.Value){
			alert('전표가 존재하지 않습니다.');
		} else {			
			for(var i=0; i<=10; i++) {
				fn_Input();
			}
		}
	} else {	
		//ds_BJIZ110.SetColumn(ds_BJIZ110.Row, "RPIVN", trim(ed_SGBVN.Value)); 						
		ed_SGBVN.Value = ds_BJIZ110.GetColumn(0,"RPIVN");
		st_VNAME.Text = ds_BJIZ110.GetColumn(0,"VNAME");	
		//MainGrid.SetCellProp("RPIPN",1,"edit","normal");	
	}

	if (str == 'T'){  //정상조회
		alert(setPopMessage("1004"));
		setMessage("1004");	
	}else if (str == 'B'){   //data값이 없을때

		alert(setPopMessage("1003"));
		setMessage("1003");	
	}else if (str == 'E'){   //error발생시
		alert(setPopMessage("1002"));
		setMessage("1002");	
	}
}

/********************************************************************** 
 * 내용설명 : insert, update, delete
 **********************************************************************/ 
function setBJIZ110(){

	if(!validationChk()) return;

	if(length(trim(ed_SGBVN.Text))==0){
		alert("업체 코드를 입력해 주세요.");
		ed_SGBVN.SetFocus();
		return false;
	}

	// var idnum = substr(G_ds_user.GetColumn(0,"USER_ID"),1,7); 사용자 아이디 취득

	var sKind		= "setBJIZ110";						
	var sMethodName = "service::psimmlib/saveBJIZ110.do";     // 서버에서 호출 될 Method 명
	var sInDataSet  = "ds_BJIZ110=ds_BJIZ110:U";               //자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_BJIZ110_CHECK=ds_BJIZ110_CHECK";     // 조회 후 돌려 받을 DataSet 명칭

	var sArgument   = "";
		sArgument  += " LIB2=" + quote(Lib2);
		sArgument  += " COMPS=" + quote(ComboBoxPlant.Value);  									// 사업분류	
		sArgument  += " RPIJPBEFORE=" + quote(ToInteger(ed_rpijp.Value)-1);       				// 전표번호	
		sArgument  += " MAJCD=" + "BJIZ110";  													// BJIZ110 전표 번호 수집 KEY

	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
}

/********************************************************************** 
 * 내용설명 : Transaction 리턴후 실행 함수
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	if (svcId = "setBJIZ110"){
		if (ds_BJIZ110_CHECK.GetRowCount() > 0){			
			for(var i=0;i<ds_BJIZ110_CHECK.GetRowCount();i++){		

				var errMessage = "'" + ds_BJIZ110_CHECK.GetColumn(i,"RPIVN") + "'";
				errMessage +=" 는 품번에 대해서 다시 확인이 필요합니다.";
				alert(errMessage);	
				/*
				for(var i=0; i<ds_BJIZ110.rowcount(); i++){
					if(trim(ds_BJIZ110.GetColumn(i,"RPIJP")) == rpijp){						
						ds_BJIZ110.SetColumn(i,"");
						continue;
					}
				}
				*/
			}	
			return;	
		}
		getBJIZ110(); 	   // 전표 조회
	}

	// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");
	// tran 성공	
	}else if(svcId=="setBJIZ110"){
		alert(setPopMessage("1001"));
		setMessage("1001");
	}	
}

function ds_BJIZ110_CanColumnChange(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	http.usewaitcursor = false;	
	if(strColumnID=="RPIPN"){
		if(varNewValue!=""){
			http.Sync = true;
			var sKind		= "getBJMZ110";		
			var sMethodName = "service::psimmlib/selectBJSZ.do"; 
			var sInDataSet  = "";
			var sOutDataSet = "ds_BJSZ110_CHECK=ds_BJSZ110_CHECK";
			var sArgument   = "";
				sArgument  += " LIB2=" + quote(Lib2);
				sArgument  += " LIB3=" + quote(Lib2);
				sArgument  += " COMPS=" + quote(ComboBoxPlant.Value); //사업부
				sArgument  += " SQLID=" + "selectBJM100";  			  				// SQL ID
				sArgument  += " RPIPN=" + quote(varNewValue);
				sArgument  += " RPICL=" + ds_BJIZ110.GetColumn(nRow, "RPICL");
			Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
			http.Sync = false;
			if (ds_BJSZ110_CHECK.GetRowCount() < 1){
				alert("등록되지 않은 품번 정보입니다. 재입력 바랍니다."); 	

				ds_BJIZ110.SetColumn(nRow, "RPICL", ""); 		
				ds_BJIZ110.SetColumn(nRow, "RPIPN", ""); 						
				return false;
			}
		}
	}


	http.usewaitcursor = true;
}
/********************************************************************** 
 * 내용설명 : validation 체크 1
 **********************************************************************/ 
function validationChk(){

	if(length(trim(ed_rpijp.Text))==0){
		alert("전표 번호를 입력해 주세요.");
		ed_rpijp.SetFocus();
		return false;
	}
	/*
	if(length(trim(ed_SGBVN.Text))==0){
		alert("업체 코드를 입력해 주세요.");
		ed_SGBVN.SetFocus();
		return false;
	}
	*/

	return true;
}

/********************************************************************** 
 * 내용설명 : 업체 검색
 **********************************************************************/ 
function MainDiv_ed_SGBVN_OnChanged(obj,strText)
{
	http.Sync 	= true;
	var sKind		= "getVENNAM";		
	var sMethodName = "service::pscomlib/selectVENNAM.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_VENNAM=ds_VENNAM";
	var sArgument   = "";
		sArgument  += " LIB1=" + quote(Lib1);
		sArgument  += " VNDNR=" + quote(strText);
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	if (ds_VENNAM.GetRowCount() != 1){
		alert(setPopMessage("4674"));
		setMessage("4674");	
		st_VNAME.Text = "";
		ed_SGBVN.SetFocus();
		return;
	}
	st_VNAME.Text = ds_VENNAM.GetColumn(0,"VNAME");	
	getBJIZ110();
}

