function fn_Search()
{
	if(ComboBoxPlant.value=""){
		alert("사업부를 선택하십시요.");
		break;
	}
	if(ca_ISIDT_ST.value=""){
		alert("입고 년월을 입력하십시요.");
		break;
	}
	if(ca_ISIDT_ED.value=""){
		alert("입고 년월을 입력하십시요.");
		break;
	}
	getBJOZ116Q();

}

