#include "script::lib_script_common.js";
#include "script::Grid_Script.js";
#include "script::spare_common.js";
var strSeparator = "	";
var Lib1 = G_ds_user.GetColumn(0,"LIB1"); // cigma library(pereslib,psreslib)
var Lib2 = G_ds_user.GetColumn(0,"LIB2"); // 회사 library(psedblib,pscdblib)
var Lib3 = G_ds_user.GetColumn(0,"LIB3"); // 사업부 library(psedblib,pscdblib,pshdblib,pssdblib)
var company = G_ds_user.GetColumn(0,"COMPANY");
var comps = G_ds_user.GetColumn(0,"PLANT_CD");

function on_LoadCompleted(obj){
	getTitle();	
	getDesign();
	gFcOnTopButtonColor("1"); //조회, 추가, 저장
	getComCode(); //공장구분 선택
 }

function getComCode(){
	var sKind		= "getComCode";						
	var sMethodName = "service::login/selectComCode.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ComCode_PLNT=ds_ComCode_PLNT";
	var sArgument   = "";
		sArgument   += " CLASS='PLNT'";
		sArgument   += " CODE1=" + quote(company);		
	http.Sync=true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync=false;
	ComboBoxPlant.Value=comps;
	if(company=="PS"){
		ComboBoxPlant.Value="C1";
	}
	ComboBoxPlant.Enable = false; 
}
function getDesign(){
	//MainGrid.ToolTipText = setPopMessage("2053");
	MainGrid.BkColor = G_ds_design.GetColumn(0,"BkColor");
	MainGrid.BkColor2 = G_ds_design.GetColumn(0,"BkColor2");
	MainGrid.BkSelColor = G_ds_design.GetColumn(0,"BkSelColor");
	MainGrid.Face3dColor = G_ds_design.GetColumn(0,"Face3dColor");
	MainGrid.BoldHead = G_ds_design.GetColumn(0,"BoldHead");
	MainGrid.HeadHeight = G_ds_design.GetColumn(0,"HeadHeight");
	MainGrid.Width = G_ds_design.GetColumn(0,"Width");
	MainGrid.UserData = MainGrid.Height;

	searchBar.Width = G_ds_design.GetColumn(0,"Width");
	searchBar.Top = G_ds_design.GetColumn(0,"SBTop");	

	//Static0.BkColor = G_ds_design.GetColumn(0,"BTBkColor2");
	//Static3.BkColor = G_ds_design.GetColumn(0,"BTBkColor2");
}

function getCGMPARMHC(){
	var sKind		= "getCGMPARMHC";						
	var sMethodName = "service::pscomlib/selectCGMPARMHC.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_CGMPARMHC=ds_CGMPARMHC";
	var sArgument   = "";
		sArgument += " LIB1=" + quote(Lib1);
	http.Sync=true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync=false;
}

function getBODE126Q(){

	var p_vendr1,p_vendr2,p_pspno1,p_pspno2;
		p_vendr1 = iif (length(trim(ed_VENDR_F.Text)) == 0, '', trim(ed_VENDR_F.Text));
		p_vendr2 = iif (length(trim(ed_VENDR_T.Text)) == 0 , '999999', trim(ed_VENDR_T.Text));
		p_pspno1 = iif(length(trim(ed_PSPNO_F.Text)) == 0 , '000-000', trim(ed_PSPNO_F.Text));
		p_pspno2 = iif(length(trim(ed_PSPNO_T.Text)) == 0 , '999-999', trim(ed_PSPNO_T.Text));

	//창고코드
	http.Sync 	= true;
	var sKind		= "getSC008PR";						
	var sMethodName = "service::pscomlib/selectSC008PR.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_SC008PR=ds_SC008PR";
	var sArgument   = "";
		sArgument   += " DTANO='HAC'";
		sArgument   += " LIB1=" + quote(Lib1);	
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync 	= false;
	var arrHouseT = trim(ds_SC008PR.GetColumn(0,"CTLDT"));
	var arrHouse = "('";
	for (var i=0 ; toNumber(length(arrHouseT)) > i ; i++){
		if (i == length(arrHouseT)-1){
			arrHouse = arrHouse+substr(arrHouseT,i,1) + "')";
		}else{
			arrHouse = arrHouse+substr(arrHouseT,i,1) + "','";
		}
	}
	//무상재고창고코드
	http.Sync 	= true;
	var sKind		= "getSC008PR";						
	var sMethodName = "service::pscomlib/selectSC008PR.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_SC008PR=ds_SC008PR";
	var sArgument   = "";
		sArgument   += " DTANO='HAP'";
		sArgument   += " LIB1=" + quote(Lib1);	
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync 	= false;
	var arrHouse2T = trim(ds_SC008PR.GetColumn(0,"CTLDT"));
	var arrHouse2 = "('";
	for (var i=0 ; toNumber(length(arrHouse2T)) > i ; i++){
		if (i == length(arrHouse2T)-1){
			arrHouse2 = arrHouse2+substr(arrHouse2T,i,1) + "')";
		}else{
			arrHouse2 = arrHouse2+substr(arrHouse2T,i,1) + "','";
		}
	}

	getCGMPARMHC();
	var sandt= ds_CGMPARMHC.GetColumn(0,"ADSDT");

	var sKind		= "getMST090PF";		
	var sMethodName = "service::pscomlib/selectMST090PF.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_MST090PF=ds_MST090PF";
	var sArgument   = "";
		sArgument += " LIB2=" + quote(Lib2);
		sArgument += " COMPS=" + quote(ComboBoxPlant.Value); //사업부
		sArgument += " CHKDT=" + quote("PDATE"); //시작일자 PARAM
		sArgument += " CHKCNT=" + quote(11); //row수
		sArgument += " STDDT=" + quote(sandt); //시작일자
	http.Sync=true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync=false;

	var sKind		= "getBODE126Q";						
	var sMethodName = "service::psimmlib/selectBODE126Q.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                            				// 자료 저장 시 보낼 DataSet 명칭
	var	sOutDataSet = "ds_BODE126Q=ds_BODE126Q";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
		sArgument += "@LIB1=" + quote(Lib1);
		sArgument += "@LIB2=" + quote(Lib2);
		sArgument += "@COMPS=" + quote(ComboBoxPlant.Value); //사업부
		sArgument += "@VENDRF=" + quote(p_vendr1); //작업그룹TO
		sArgument += "@VENDRT=" + quote(p_vendr2); //작업그룹TO
		sArgument += "@PSPNOF=" + quote(p_pspno1); //작업그룹TO
		sArgument += "@PSPNOT=" + quote(p_pspno2); //작업그룹TO
		sArgument += "@GUB=" + quote(cmb_GUB.Value); //조회구분
		sArgument += "@HOUSE=" + quote(arrHouse); //창고코드
		sArgument += "@FHOUSE=" + quote(arrHouse2); //무상재고창고코드
	var str = dialog("Common::PGMLOAD_IMM.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");
	if (str == 'T'){  //정상조회
		alert(setPopMessage("1004"));
		setMessage("1004");
	}else if (str == 'B'){   //data값이 없을때
		alert(setPopMessage("1003"));
		setMessage("1003");	
	}else if (str == 'E'){   //error발생시
		alert(setPopMessage("1002"));
		setMessage("1002");	
	}

	for(i=0; i<11; i++){
		MainGrid.SetCellProp("Head",14+i, "text", substr(ds_MST090PF.GetColumn(i,"PRCDT"),4,2)+"/"+substr(ds_MST090PF.GetColumn(i,"PRCDT"),6,2));
	}
}

