function fn_Search()
{
	if(ed_S01.Text == "Y"){
		var strURL  = "BOM521QD.xml";
		var strArg  = " TRANSGB=" + quote("1"); 
		var rtnVal = Dialog(strURL,strArg);
		if( rtnVal == "" || rtnVal == "0" ) {
			ed_S01.Text = "N";
			prtno_sel = "";
			return;
		}else{
			prtno_sel = rtnVal;
		}
		getBOM521Q();
	}else{
		getBOM521Q();
	}
}

