/********************************************************************** 
 * 내용설명 : 화면로드
 * 주요변수 : 
 **********************************************************************/ 
function on_LoadCompleted(obj){
	getTitle();	
	getDesign();
	getComCode();

	var ym0 = substr(nowDate,0,4)+"/"+substr(nowDate,4,2);
	var ym1 = substr(AddMonth(nowDate,1),0,4)+"/"+substr(AddMonth(nowDate,1),4,2);
	var ym2 = substr(AddMonth(nowDate,2),0,4)+"/"+substr(AddMonth(nowDate,2),4,2);
	var ym3 = substr(AddMonth(nowDate,3),0,4)+"/"+substr(AddMonth(nowDate,3),4,2);
	var ym4 = substr(AddMonth(nowDate,4),0,4)+"/"+substr(AddMonth(nowDate,4),4,2);
	var bym3 = substr(AddMonth(nowDate,-3),0,4)+"/"+substr(AddMonth(nowDate,-3),4,2);
	var bym2 = substr(AddMonth(nowDate,-2),0,4)+"/"+substr(AddMonth(nowDate,-2),4,2);
	var bym1 = substr(AddMonth(nowDate,-1),0,4)+"/"+substr(AddMonth(nowDate,-1),4,2);
	//nCell의 값은 (RowLine * Column 개수 + Col 값)

	MainGrid.SetCellProp("Head", 20, "text",ym0);
	MainGrid.SetCellProp("Head", 7, "text",ym1);
	MainGrid.SetCellProp("Head", 8, "text",ym2);
	MainGrid.SetCellProp("Head", 9, "text",ym3);
	MainGrid.SetCellProp("Head", 10, "text",ym4);

	MainGrid.SetCellProp("Head", 11, "text",bym3);
	MainGrid.SetCellProp("Head", 12, "text",bym2);
	MainGrid.SetCellProp("Head", 13, "text",bym1);

	MainGrid.SetCellProp("Head", 15, "text",ym0); 
	MainGrid.SetCellProp("Head", 16, "text",ym1); 
	MainGrid.SetCellProp("Head", 17, "text",ym2);
	MainGrid.SetCellProp("Head", 18, "text",ym3);
	MainGrid.SetCellProp("Head", 19, "text",ym4);

	//getMst040();
	gFcOnTopButtonColor("1"); //상단 버튼 활성화 여부 (기본:0,조회:1,변경:2)
	ed_CUVND1.SetFocus();
}


