 **********************************************************************/ 
function getDesign(){
	//MainGrid.ToolTipText = setPopMessage("2053");
	MainGrid.BkColor = G_ds_design.GetColumn(0,"BkColor");
	MainGrid.BkColor2 = G_ds_design.GetColumn(0,"BkColor2");
	MainGrid.BkSelColor = G_ds_design.GetColumn(0,"BkSelColor");
	MainGrid.Face3dColor = G_ds_design.GetColumn(0,"Face3dColor");
	MainGrid.BoldHead = G_ds_design.GetColumn(0,"BoldHead");
	//MainGrid.HeadHeight = G_ds_design.GetColumn(0,"HeadHeight");  //22
	MainGrid.Width = G_ds_design.GetColumn(0,"Width");            //1048
	//MainGrid.Top = G_ds_design.GetColumn(0,"Top");                //64

	searchBar.Width = G_ds_design.GetColumn(0,"Width");           //1048
	searchBar.Height = G_ds_design.GetColumn(0,"SBHeight");	      //30
	searchBar.Top = G_ds_design.GetColumn(0,"SBTop");	          //32
}

/********************************************************************** 
 * 내용설명 : 사업부 선택
 * 주요변수 : 
 **********************************************************************/ 
function getComCode(){
	http.Sync 	= true;

	var sKind		= "getComCode";						
	var sMethodName = "service::login/selectComCode.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ComCode_PLNT=ds_ComCode_PLNT";
	var sArgument   = "";
		sArgument   += " CLASS='PLNT'";
		sArgument   += " CODE1=" + quote(company);		
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	cb_searchCodeName1.Value = comps;
}

/********************************************************************** 
 * 내용설명 : 화면조회
 * 주요변수 : 
 **********************************************************************/ 
function getBOMZ562Q(){
	var p_vnd1,p_vnd2;
	p_vnd1 = iif (length(trim(ed_CUVND1.Text)) == 0, '', trim(ed_CUVND1.Text));
	p_vnd2 = iif (length(trim(ed_CUVND2.Text)) == 0 , '999', trim(ed_CUVND2.Text));
	var p_prt1,p_prt2;
	p_prt1 = iif (length(trim(ed_PRTNO_from.Text)) == 0, '', trim(ed_PRTNO_from.Text));
	p_prt2 = iif (length(trim(ed_PRTNO_to.Text)) == 0 , '999999999999999', trim(ed_PRTNO_to.Text));

	var cur_yyyy;
	var bef_yyyy;
	if(toNumber(substr(nowDate,4,2)) >= 7){                     // 7월,8월,9월,10월,11월,12월
		cur_yyyy = substr(nowDate,0,4);
		bef_yyyy = substr(nowDate,0,4);
	}else if(toNumber(substr(nowDate,4,2)) >= 5){               // 5월,6월
		cur_yyyy = substr(nowDate,0,4);
		bef_yyyy = substr(AddMonth(nowDate,-12),0,4);
	}else{                                                      //1월,2월,3월,4월
		cur_yyyy = substr(AddMonth(nowDate,-12),0,4);
		bef_yyyy = substr(AddMonth(nowDate,-12),0,4);
	}
	var bef_mm1 = substr(AddMonth(nowDate,-1),4,2);  // m-1월 (출고현황)
	var bef_mm2 = substr(AddMonth(nowDate,-2),4,2);  // m-2월 (출고현황)
	var bef_mm3 = substr(AddMonth(nowDate,-3),4,2);  // m-3월 (출고현황)

	var sKind		= "getBOMZ562Q";						
	var sMethodName = "service::psimmlib/selectBOMZ562Q.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_BOMZ562Q=ds_BOMZ562Q";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
	sArgument += "@LIB1=" + quote(Lib1);
	sArgument += "@LIB2=" + quote(Lib2);
	sArgument += "@COMPS=" + quote(cb_searchCodeName1.Value);
	sArgument += "@PRVND1=" + quote(p_vnd1);
	sArgument += "@PRVND2=" + quote(p_vnd2);
	sArgument += "@EXPNO1=" + quote(p_prt1);
	sArgument += "@EXPNO2=" + quote(p_prt2);
	sArgument += "@CURYM=" + quote(substr(nowDate,0,6));
	sArgument += "@CYYYY=" + quote(cur_yyyy);  //출고현항 계산년
	sArgument += "@BYYYY=" + quote(bef_yyyy);  //출고현항 계산년
	sArgument += "@BMM1=" + quote(substr(AddMonth(nowDate,-1),4,2));    // m-1월 (출고현황)
	sArgument += "@BMM2=" + quote(substr(AddMonth(nowDate,-2),4,2));    // m-2월 (출고현황)
	sArgument += "@BMM3=" + quote(substr(AddMonth(nowDate,-3),4,2));    // m-3월 (출고현황)
	sArgument += "@YM01=" + quote(substr(AddMonth(nowDate,1),0,4)+substr(AddMonth(nowDate,1),4,2)); //발주현황(미입고) 계산년월
	sArgument += "@YM02=" + quote(substr(AddMonth(nowDate,2),0,4)+substr(AddMonth(nowDate,2),4,2)); //발주현황(미입고) 계산년월
	sArgument += "@YM03=" + quote(substr(AddMonth(nowDate,3),0,4)+substr(AddMonth(nowDate,3),4,2)); //발주현황(미입고) 계산년월
	sArgument += "@YM04=" + quote(substr(AddMonth(nowDate,4),0,4)+substr(AddMonth(nowDate,4),4,2)); //발주현황(미입고) 계산년월

	sArgument += "@COMPANY=" + quote(company);
	//sArgument += "@NDATE=" + quote(nowDate);
	var str = dialog("Common::PGMLOAD_IMM.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");	
	//Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
	if (str == 'T'){  //정상조회
		alert(setPopMessage("1004"));
		setMessage("1004");	
	}else if (str == 'B'){   //data값이 없을때
		alert(setPopMessage("1003"));
		setMessage("1003");	
	}else if (str == 'E'){   //error발생시
		alert(setPopMessage("1002"));
		setMessage("1002");	
	}
}


/********************************************************************** 
 * 내용설명 : 엑셀파일로 저장
 * 주요변수 : 
 **********************************************************************/ 
function setExcel()
{
	var fName   = "수입현조품 과부족 이상유무 조회 ";
	var arrSheets  =  Array();  

	arrSheets[0]  = "MainGrid|수입현조품 과부족 이상유무 조회 ";
	createExcelSheetS2(FileDialog0, fName ,arrSheets);

}


/********************************************************************** 
 * 내용설명 : Transaction 리턴후 실행 함수
 * 주요변수 : 
 * 			- sMethodName	Transaction 고유 ID
 * 			- ErrorCode 	에러코드
 * 			- ErrorMSG 		에러 메세지
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");

	}else if(svcId=="getBZZE401Q"){
		// 조회건수가 없을경우
		if(ds_BZZE401Q.rowcount <= 0){
			alert(setPopMessage("1003"));
			setMessage("1003");	
		// 정상 조회
		} else {
			alert(setPopMessage("1004"));
			setMessage("1004");				
		}
	}
}

