/********************************************************************** 
 * 내용설명 : 사업부 선택
 * 주요변수 : 
 **********************************************************************/ 
function getComCode(){

	http.Sync 	= true;

	var sKind		= "getComCode";						
	var sMethodName = "service::login/selectComCode.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ComCode_PLNT=ds_ComCode_PLNT";
	var sArgument   = "";
		sArgument   += " CLASS='PLNT'";
		sArgument   += " CODE1=" + quote(company);		
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	ComboBoxPlant.Value = company;
}

/********************************************************************** 
 * 내용설명 : as400 system 날짜 get
 * 주요변수 : 
 **********************************************************************/ 
function getSYSDATE(){
	var sKind		= "getSYSDATE";						
	var sMethodName = "service::pscomlib/selectSYSDATE.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_SYSDATE=ds_SYSDATE";
	var sArgument   = "";
	http.Sync=true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync=false;	
}

/********************************************************************** 
 * 내용설명 : 화면조회
 * 주요변수 : 
 **********************************************************************/ 
function getBSME158R(){
	var p_vnd1,p_vnd2;
	p_vnd1 = iif (length(trim(ed_CUVND1.Text)) == 0, '0', trim(ed_CUVND1.Text));
	p_vnd2 = iif (length(trim(ed_CUVND2.Text)) == 0 , '99999999', trim(ed_CUVND2.Text));
	getSYSDATE(); //SYSTEM 날짜
	var sysDt =ds_SYSDATE.GetColumn(0,"DT");
	var systm =ds_SYSDATE.GetColumn(0,"TM");

	var sKind		= "getBSME158R";						
	var sMethodName = "service::psimmlib/selectBSME158R.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_BSME158R=ds_BSME158R";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
	sArgument += " LIB1=" + quote(Lib1);
	sArgument += " LIB2=" + quote(Lib2);
	sArgument += " COMPS=" + quote(ComboBoxPlant.Value);
	sArgument += " CONNO1=" + quote(p_vnd1);
	sArgument += " CONNO2=" + quote(p_vnd2);
	http.Sync = true;
	//var str = dialog("Common::PGMLOAD_IMM.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");	
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
	http.Sync = false;
	//ubi0.setJrfFileDir("C:/eclipse/workspace/DENSO/WebContent"+"/ui/PSIMMLIB/REPORT/");
	ubi0.setJrfFileDir(G_baseURL+"ui/PSIMMLIB/REPORT/");
	ubi0.setJrfFileName("BSME158R.jrf");
	ubi0.SetDataset("ds_BSME158R", "ds_BSME158R");	
	//ubi0.SetDataset("ds_SMBZ122R_TOT", "ds_SMBZ122R_TOT");	
	ubi0.SetArgument("COMPS", ComboBoxPlant.Value);
	Ubi0.SetArgument("SYSDATE",sysDt);
	Ubi0.SetArgument("SYSTIME",systm);	
	ubi0.Retrieve();
}


/********************************************************************** 
 * 내용설명 : 엑셀파일로 저장
 * 주요변수 : 
 **********************************************************************/ 
function setExcel()
{
	var fName   = "무상사급 재고조사LIST";
	var arrSheets  =  Array();  

	arrSheets[0]  = "MainGrid|무상사급 재고조사LIST";
	createExcelSheetS2(FileDialog0, fName ,arrSheets);

}


/********************************************************************** 
 * 내용설명 : Transaction 리턴후 실행 함수
 * 주요변수 : 
 * 			- sMethodName	Transaction 고유 ID
 * 			- ErrorCode 	에러코드
 * 			- ErrorMSG 		에러 메세지
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");

	}else if(svcId=="getBSME158R"){
		// 조회건수가 없을경우
		if(ds_BSME158R.rowcount <= 0){
			alert(setPopMessage("1003"));
			setMessage("1003");	
		// 정상 조회
		} else {
			alert(setPopMessage("1004"));
			setMessage("1004");				
		}	
	}
}

function Grid0_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
	if(nCell != 0) lib_grid_SetGridSort(obj,nCell);
}

function Grid0_OnKeyDown(obj,nChar,bShift,bCtrl,bAlt,nLLParam,nHLParam)
{
	// Ctrl + C
	if(bCtrl == 1 && nChar == 67) {

		// grd_Standard.AreaSelect = true;
		grd_fn_ClipboardCopy(obj, strSeparator);

	}
}

