/********************************************************************** 
 * 내용설명 : 화면로드
 * 주요변수 : 
 **********************************************************************/ 
function on_LoadCompleted(obj){
	getTitle();	
	getDesign();
	getComCode();
	//getMst040();
	gFcOnTopButtonColor("1"); //상단 버튼 활성화 여부 (기본:0,조회:1,변경:2)
	cal_IMHYM.Value = nowDate;
	cmb_CLSGU.SetFocus();
}


/********************************************************************** 
 * 내용설명 : 공통디자인 설정
 * 주요변수 : 
 **********************************************************************/ 
function getDesign(){
	//MainGrid.ToolTipText = setPopMessage("2053");
	MainGrid.BkColor = G_ds_design.GetColumn(0,"BkColor");
	MainGrid.BkColor2 = G_ds_design.GetColumn(0,"BkColor2");
	MainGrid.BkSelColor = G_ds_design.GetColumn(0,"BkSelColor");
	MainGrid.Face3dColor = G_ds_design.GetColumn(0,"Face3dColor");
	MainGrid.BoldHead = G_ds_design.GetColumn(0,"BoldHead");
	MainGrid.HeadHeight = G_ds_design.GetColumn(0,"HeadHeight");  //22
	MainGrid.Width = G_ds_design.GetColumn(0,"Width");            //1048
	MainGrid.Top = G_ds_design.GetColumn(0,"Top");                //64

	searchBar.Width = G_ds_design.GetColumn(0,"Width");           //1048
	searchBar.Height = G_ds_design.GetColumn(0,"SBHeight");	      //29
	searchBar.Top = G_ds_design.GetColumn(0,"SBTop");	          //32
}

/********************************************************************** 
 * 내용설명 : 사업부 선택
 * 주요변수 : 
 **********************************************************************/ 
function getComCode(){

	http.Sync 	= true;

	var sKind		= "getComCode";						
	var sMethodName = "service::login/selectComCode.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ComCode_PLNT=ds_ComCode_PLNT";
	var sArgument   = "";
		sArgument   += " CLASS='PLNT'";
		sArgument   += " CODE1=" + quote(company);		
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	ComboBoxPlant.Value = comps;
}

/********************************************************************** 
 * 내용설명 : 화면조회
 * 주요변수 : 
 **********************************************************************/ 
function getBSSE101Q(){
	if(ed_PRCGUB.Text != 'Y' && ed_PRCGUB.Text != 'N'){
		ed_PRCGUB.Text = "N";
	}
	if(length(trim(cal_IMHYM.Value)) < 8){
		alert(setPopMessage("2528"));  //[[확인] 마감년월을 확인바랍니다.
		setMessage("2528");	
		cal_IMHYM.SetFocus();
		return;
	}
	if(length(trim(cmb_CLSGU.Value)) < 1){
		alert(setPopMessage("2033"));  //[알림] 구분 코드를 입력해 주세요.
		setMessage("2033");	
		cmb_CLSGU.SetFocus();
		return;
	}
	/*
	if(cmb_CLSGU.Value == "1"){
		MainGrid.SetCellProp("Head", 11, "text", "재고조정");
		MainGrid.SetCellProp("body", 17, "colid", "CUCQY");
		MainGrid.SetCellProp("body", 17, "Mask", "#,##0");
		// <cell align="right" col="17" colid="RATE" display="number" Mask="#,##0.#0"/>
		MainGrid.SetCellProp("summary", 10, "align", "right");
		MainGrid.SetCellProp("summary", 10, "display", "number");
		MainGrid.SetCellProp("summary", 10, "expr", "sum('CUCQY')");
	}else{
		MainGrid.SetCellProp("Head", 11, "text", "증감율");
		MainGrid.SetCellProp("body", 17, "colid", "RATE");
		MainGrid.SetCellProp("body", 17, "Mask", "#,##0.#0");
		MainGrid.SetCellProp("summary", 10, "display", "normal");
		MainGrid.SetCellProp("summary", 10, "expr", "");
	}

	var imhyy = substr(cal_IMHYM.Value,0,4);
	if(substr(cal_IMHYM.Value,4,2) <= '03'){
		imhyy = toNumber(imhyy)-1;  //회계년도
	}
	var imqmm_b = "A.IMQ"+substr(AddMonth(cal_IMHYM.Value,-1),4,2);
	if(substr(cal_IMHYM.Value,4,2) == '04'){
		imqmm_b = "A.IMQ00";  //전월재고
	}
	var imimm = "A.IMI"+substr(cal_IMHYM.Value,4,2);  //입고
	var imomm = "A.IMO"+substr(cal_IMHYM.Value,4,2);  //출고
	var imqmm = "A.IMQ"+substr(cal_IMHYM.Value,4,2);  //당월재고
	var imcmm = "A.IMC"+substr(cal_IMHYM.Value,4,2);  //재고조정
	*/	
	var imhyy = substr(cal_IMHYM.Value,0,4);
	var imhmm = substr(cal_IMHYM.Value,4,2);
	if(imhmm != '03' && imhmm != '09'){
		alert(setPopMessage("5015"));  // [확인] 실사년월을 확인바랍니다.
		setMessage("5015");	
		cal_IMHYM.SetFocus();
		return;
	}

	var sKind		= "getBSSE101Q";						
	var sMethodName = "service::psimmlib/selectBSSE101Q.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_BSSE101Q=ds_BSSE101Q";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
	sArgument += "@LIB1=" + quote(Lib1);
	sArgument += "@LIB2=" + quote(Lib2);
	sArgument += "@COMPANY=" + quote(company);
	sArgument += "@COMPS=" + quote(ComboBoxPlant.Value);
	sArgument += "@CLSGU=" + quote(cmb_CLSGU.Value);
	sArgument += "@IMHYY=" + quote(imhyy);
	sArgument += "@IMHMM=" + quote(imhmm);
	sArgument += "@PRCGUB=" + quote(ed_PRCGUB.Value);
	sArgument += "@D30PR=" + quote("F#D30PR");
	sArgument += "@YMD=" + quote(substr(cal_IMHYM.Value,0,6)+"31");
	http.TimeOut = 900;
	var str = dialog("Common::PGMLOAD_IMM.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");	
	//Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
	http.TimeOut = 300;
	if (str == 'T'){  //정상조회
		alert(setPopMessage("1004"));
		setMessage("1004");	
	}else if (str == 'B'){   //data값이 없을때
		alert(setPopMessage("1003"));
		setMessage("1003");	
	}else if (str == 'E'){   //error발생시
		alert(setPopMessage("1002"));
		setMessage("1002");	
	}
}


/********************************************************************** 
 * 내용설명 : 엑셀파일로 저장
 * 주요변수 : 
 **********************************************************************/ 
function setExcel()
{
	var fName   = "자재 실사후 재고 집계표";
	var arrSheets  =  Array();  

	arrSheets[0]  = "MainGrid|자재 실사후 재고 집계표";
	createExcelSheetS2(FileDialog0, fName ,arrSheets);

}


/********************************************************************** 
 * 내용설명 : Transaction 리턴후 실행 함수
 * 주요변수 : 
 * 			- sMethodName	Transaction 고유 ID
 * 			- ErrorCode 	에러코드
 * 			- ErrorMSG 		에러 메세지
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");

	}else if(svcId=="getBSSE101Q"){
		// 조회건수가 없을경우
		if(ds_BSSE101Q.rowcount <= 0){
			alert(setPopMessage("1003"));
			setMessage("1003");	
		// 정상 조회
		} else {
			alert(setPopMessage("1004"));
			setMessage("1004");				
		}	
	}
}

function Grid0_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
	if(nCell <= 8) lib_grid_SetGridSort(obj,nCell);
}

function Grid0_OnKeyDown(obj,nChar,bShift,bCtrl,bAlt,nLLParam,nHLParam)
{
	// Ctrl + C
	if(bCtrl == 1 && nChar == 67) {

		// grd_Standard.AreaSelect = true;
		grd_fn_ClipboardCopy(obj, strSeparator);

	}
}

