	MainGrid.UserData = MainGrid.Height;
	gFcOnTopButtonColor("2.2"); //상단 버튼 활성화 여부  1:조회,excel                1.1:조회,발행,excel                 1.2:조회     
	                          //                       2.1:조회,추가,삭제,저장     2.2:조회,추가,삭제,저장,excel       2.3:조회,저장,excel
	                          //                       2.4:조회,추가,저장          2.5:저장                            2.6:조회,삭제,저장
	                          //                       2.7:조회,추가,저장,excel 		
	ed_JPNNO.SetFocus();

}

/********************************************************************** 
 * 내용설명 : 공통디자인 설정
 * 주요변수 : 
 **********************************************************************/ 
function getDesign(){
	//MainGrid.ToolTipText = setPopMessage("2053");
	MainGrid.BkColor = G_ds_design.GetColumn(0,"BkColor");
	MainGrid.BkColor2 = G_ds_design.GetColumn(0,"BkColor2");
	MainGrid.BkSelColor = G_ds_design.GetColumn(0,"BkSelColor");
	MainGrid.Face3dColor = G_ds_design.GetColumn(0,"Face3dColor");
	MainGrid.BoldHead = G_ds_design.GetColumn(0,"BoldHead");
	MainGrid.HeadHeight = G_ds_design.GetColumn(0,"HeadHeight");  //22
	MainGrid.Width = G_ds_design.GetColumn(0,"Width");            //1048
	//MainGrid.Top = G_ds_design.GetColumn(0,"Top");                //64

	searchBar.Width = G_ds_design.GetColumn(0,"Width");           //1048
	//searchBar.Height = G_ds_design.GetColumn(0,"SBHeight");	      //29
	searchBar.Top = G_ds_design.GetColumn(0,"SBTop");	          //32
}

/********************************************************************** 
 * 내용설명 : 사업부 선택
 * 주요변수 : 
 **********************************************************************/ 
function getComCode(){

	http.Sync 	= true;

	var sKind		= "getComCode";						
	var sMethodName = "service::login/selectComCode.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ComCode_PLNT=ds_ComCode_PLNT";
	var sArgument   = "";
		sArgument   += " CLASS='PLNT'";
		sArgument   += " CODE1=" + quote(company);		
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	ComboBoxPlant.Value = comps;


}


/********************************************************************** 
 * 내용설명 : 화면조회
 * 주요변수 : 
 **********************************************************************/ 
function getBUUZ160(){

	if(length(trim(ed_JPNNO.text)) < 1){
		alert(setPopMessage("4012")); //[확인] 전표번호를 확인바랍니다.
		setMessage("4012");
		ed_JPNNO.SetFocus();
		return ;
	}
	var sKind		= "getBUUZ160";				
	var sMethodName = "service::psimmlib/selectBUUZ160.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_BUUZ160=ds_BUUZ160";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";	
	sArgument += " LIB1=" + quote(Lib1);
	sArgument += " LIB2=" + quote(Lib2);
	sArgument += " JPNNO=" + quote(trim(ed_JPNNO.Text));
	//sArgument += " CONNO=" + quote(ed_CONNO.Text);
	//sArgument += " TRNDT=" + quote(ca_TRNDT.Value);
	//sArgument += " HOUSE=" + quote(ed_HOUSE.Text);
	//var str = dialog("Common::PGMLOAD_IMM.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");	
	http.Sync = true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync = false;
	if(ds_BUUZ160.GetRowCount() > 0){
		ed_CONNO.Text = ds_BUUZ160.GetColumn(0,"CONNO");
		st_VNAME.Text = ds_BUUZ160.GetColumn(0,"VNAME");
		ca_TRNDT.Value = ds_BUUZ160.GetColumn(0,"TRNDT");
		ed_HOUSE.Text = ds_BUUZ160.GetColumn(0,"HOUSE");
	}
}


/********************************************************************** 
 * 내용설명 : 저장
 * 주요변수 : 
 * 			
 **********************************************************************/ 

 function setBUUZ160(){
 	if(!validationChk()){
		return;
	}
	var sKind		= "setBUUZ160";						
	var sMethodName = "service::psimmlib/saveBUUZ160.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "ds_BUUZ160=ds_BUUZ160:U";                      // 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_BUUZ160_Check=ds_BUUZ160_Check";							// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
	sArgument += " LIB1=" + quote(Lib1);
	sArgument += " LIB2=" + quote(Lib2);
	sArgument += " JPNNO=" + quote(trim(ed_JPNNO.Text));
	sArgument += " CONNO=" + quote(ed_CONNO.Text);
	sArgument += " TRNDT=" + quote(ca_TRNDT.Value);
	sArgument += " HOUSE=" + quote(ed_HOUSE.Text);

	http.Sync=true;
	//var str = dialog("Common::PGMLOAD_IMM.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");	
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
	http.Sync=false;
 }

/********************************************************************** 
 * 내용설명 : Transaction 리턴후 실행 함수
 * 주요변수 : 
 * 			- sMethodName	Transaction 고유 ID
 * 			- ErrorCode 	에러코드
 * 			- ErrorMSG 		에러 메세지
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){

	if (svcId == "setBUUZ160"){
		if (ds_BUUZ160_Check.GetRowCount() > 0){			
			for(var i=0;i<ds_BUUZ160_Check.GetRowCount();i++){		
				alert(ds_BUUZ160_Check.GetColumn(i,"PSPNO")+" : "+ds_BUUZ160_Check.GetColumn(i,"RTMSG"));	
			}
		}
	}
	// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");
	}else {
		alert(setPopMessage("1001"));
		setMessage("1001");	
	}
}
/********************************************************************** 
 * 내용설명 : 마감여부 체크
 **********************************************************************/ 
function MST040Chk(){
	var sKind		= "getMST040PF";						
	var sMethodName = "service::pscomlib/selectMST040.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_MST040PF=ds_MST040PF";   					// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
	sArgument += " LIB2=" + quote(Lib2);
	sArgument += " COMPS=" + quote(company);
	sArgument += " MAJCD=" + quote("BUU160PF");
	http.Sync 		= true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync 		= false;
	if(ds_MST040PF.GetRowCount() > 0){
		ed_MJINT.Text = ds_MST040PF.GetColumn(0,"MJINT");
		/*
		if(toNumber(ds_MST040PF.GetColumn(0,"MJINT")) != 0){
			alert(setPopMessage("5014"));   //[알림] 이미 마감되었습니다.
			setMessage("5014");	
			return false;
		}
		*/
	}
	return true;
}
/********************************************************************** 
 * 내용설명 : 컴포넌트 validation 체크
 **********************************************************************/ 
function validationChk(){
	var result = true;
	if(length(trim(ed_JPNNO.text)) < 1){
		alert(setPopMessage("4012")); //[확인] 전표번호를 확인바랍니다.
		setMessage("4012");
		ed_JPNNO.SetFocus();
		return false;
	}
	if(length(ed_CONNO.Text) < 1 || st_VNAME.Text == ""){
		alert(setPopMessage("4674"));
		setMessage("4674");
		ed_CONNO.SetFocus();
		return false;
	}
	if(length(ca_TRNDT.Value) < 8){
		alert(setPopMessage("4027"));  //  [확인]전표일자를 확인바랍니다.
		setMessage("4027");
		ca_TRNDT.SetFocus();
		return false;
	}
	if(length(trim(ed_HOUSE.text)) < 1){
		alert(setPopMessage("1513"));  //[확인] 창고를 획인하세요
		setMessage("1513");	
		ed_HOUSE.SetFocus();
		return false;
	}	
	return true;
}


