function fn_Favorite()
{
	alert("fn_Favorite");
}

function fn_Search()
{
	ed_Action.Text = "Q";
	MST040Chk();
	getBUUZ160();
}

function fn_Input()
{
	if(checkInsertCount("ds_BUUZ160")== false) return;
	if(length(trim(ed_JPNNO.text)) < 1){
		alert(setPopMessage("4012")); //[확인] 전표번호를 확인바랍니다.
		setMessage("4012");
		ed_JPNNO.SetFocus();
		return ;
	}

	ed_Action.Text = "A";
	if(ds_BUUZ160.rowcount < 1){
		getBUUZ160();
	}
	if(!validationChk()){
		return;
	}
	MST040Chk();
	if(ed_MJINT.Text != "0"){
		alert(setPopMessage("5014"));   //[알림] 이미 마감되었습니다.
		setMessage("5014");	
		return;
	}
	ds_BUUZ160.AddRow();

	displayDiv.ed_PSPNO.Enable = true;
	displayDiv.ed_PSPNO.SetFocus();
}

function fn_Delete()
{
	if(!validationChk()) return;
	MST040Chk();
	if(ed_MJINT.Text != "0"){
		alert(setPopMessage("5014"));   //[알림] 이미 마감되었습니다.
		setMessage("5014");	
		return;
	}
	ds_BUUZ160.DeleteRow(ds_BUUZ160.currow);
}

function fn_Save()
{
	setBUUZ160();
}


