/********************************************************************** 
 * 내용설명 : 엑셀파일로 저장
 * 주요변수 : 
 **********************************************************************/ 
function setExcel()
{
	var fName   = "유상사급 실사재고TX 등록";
	var arrSheets  =  Array();  

	arrSheets[0]  = "MainGrid|유상사급 재고조정TX 등록";
	createExcelSheetS2(FileDialog0, fName ,arrSheets);
}

function ed_Action_OnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam)
{
	if(nChar == 13){
		switch(obj.text){
			case "A" : fn_Input(); break;
			case "Q" : fn_Search(); break;
			default : alert(setPopMessage("1016")); setMessage("1016");
		}
	}
}

function ds_BUUZ160_CanColumnChange(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{	
	if( strColumnID == "PSPNO" ){
		//기등록 check
		if(ds_BUUZ160.FindRow("PSPNO", trim(varNewValue)) >= 0){
			alert(setPopMessage("1520"));  //[경고] 이미 등록된 자료입니다.
			setMessage("1520");
			displayDiv.ed_PSPNO.SetFocus();
			return false;
		}
		//품번Check,조회
		http.Sync = true;
		var sKind		= "getBM008PR";		
		var sMethodName = "service::pscomlib/selectBM008PR.do"; 
		var sInDataSet  = "";
		var sOutDataSet = "ds_BM008PR=ds_BM008PR";
		var sArgument   = "";
			sArgument  += " LIB1=" + quote(Lib1);
			sArgument  += " ITNBR=" + quote(varNewValue);

		Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");

		if (ds_BM008PR.GetRowCount() < 1){
			alert(setPopMessage("1509"));  // [경고] Item Master에 미등록된 품번입니다.
			setMessage("1509");	
			displayDiv.ed_PSPNO.SetFocus();
			return false;
		}

		can_ITDSC = ds_BM008PR.GetColumn(0,"ITDSC");
		can_UNMSR = ds_BM008PR.GetColumn(0,"UNMSR");
		can_ITTYP = ds_BM008PR.GetColumn(0,"ITTYP");
		can_ITCLS = ds_BM008PR.GetColumn(0,"ITCLS");		
	}


}

function ds_BUUZ160_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{		
	if (strColumnID == "PSPNO"){

		ds_BUUZ160.SetColumn(nRow,"ITDSC",can_ITDSC);
		ds_BUUZ160.SetColumn(nRow,"UNMSR",can_UNMSR);
		ds_BUUZ160.SetColumn(nRow,"ITTYP",can_ITTYP);
		ds_BUUZ160.SetColumn(nRow,"ITCLS",can_ITCLS);
	}
}

function ds_BUUZ160_OnRowPosChanged(obj,nOldRow,nRow)
{
	//로우타입 insert만 ED박스 enable
	if(obj.GetRowType(obj.currow)=="insert"){
		displayDiv.ed_PSPNO.Enable=true;
		displayDiv.med_TRQTY.Enable=true;
	}else{
		displayDiv.ed_PSPNO.Enable=false;
		displayDiv.med_TRQTY.Enable=true;
	}
	//마감이되었으면 enable = false
	if(ed_MJINT.Text != 0){
		displayDiv.ed_PSPNO.Enable=false;
		displayDiv.med_TRQTY.Enable=false;
	}
}

function ed_CONNO_OnChanged(obj,strText)
{
	ds_BUUZ160.ClearData();
	http.usewaitcursor = false;
	//SUB CONTRACTOR MA CHECK
	http.Sync 	= true;
	var sKind		= "getEM040PR";		
	var sMethodName = "service::pscomlib/selectEM040PR.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_EM040PR=ds_EM040PR";
	var sArgument   = "";
		sArgument  += " LIB1=" + quote(Lib1);
		sArgument  += " CONNO=" + quote(trim(ed_CONNO.Text));
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	if (ds_EM040PR.GetRowCount() < 1){
		alert(setPopMessage("1512"));   //  [확인] SUB CONTRACTOR MASTER에 사급업체로 등록되지 않았습니다.
		setMessage("1512");	
		st_VNAME.Text = "";
		ed_CONNO.SetFocus();
		http.usewaitcursor = true;
		return ;
	}else{
		var vndnr = ds_EM040PR.GetColumn(0,"SPCOD");
		var sKind		= "getVENNAM";		
		var sMethodName = "service::pscomlib/selectVENNAM.do"; 
		var sInDataSet  = "";
		var sOutDataSet = "ds_VENNAM=ds_VENNAM";
		var sArgument   = "";
			sArgument  += " LIB1=" + quote(Lib1);
			sArgument  += " VNDNR=" + quote(vndnr);
		http.Sync 	= true;
		Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
		http.Sync 	= false;
		if (ds_VENNAM.GetRowCount() != 1){
			alert(setPopMessage("4674"));
			setMessage("4674");	
			st_VNAME.Text = "";
			ed_CONNO.SetFocus();
			http.usewaitcursor = true;
			return;
		}
		st_VNAME.Text = ds_VENNAM.GetColumn(0,"VNAME");
	}
	http.usewaitcursor = true;
}

function ed_HOUSE_OnChanged(obj,strText)
{
	ds_BUUZ160.ClearData();
	http.usewaitcursor = false;
	//창고
	var sKind		= "getSC008PR";						
	var sMethodName = "service::pscomlib/selectSC008PR.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_SC008PR=ds_SC008PR";
	var sArgument   = "";
		sArgument   += " DTANO='HAC'";      //HAC:사내창고        HAP:사급           HAR:중간창고           HAB:창고가능
		sArgument   += " LIB1=" + quote(Lib1);	
	http.Sync 	= true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync 	= false;
	var arrHouseT = trim(ds_SC008PR.GetColumn(0,"CTLDT"));
	if(IndexOf(arrHouseT,ed_HOUSE.Text) == -1){
		alert(setPopMessage("1513"));  //[확인] 창고를 획인하세요
		setMessage("1513");	
		ed_HOUSE.Text = "";
		ed_HOUSE.SetFocus();
		http.usewaitcursor = true;
		return;
	}
}

function displayDiv_med_TRQTY_OnKeyDown(obj,nChar,bShift,bCtrl,bAlt,nLLParam,nHLParam)
{
	if(nChar == 13){
		//displayDiv.ed_Flag.SetFocus();  // ed_PSPNO에 데이타셋 이벤트가 발생된 후 텟스트값이 활당되도록 포커스를 줌.
		if(length(trim(displayDiv.ed_PSPNO.Text)) > 0) {
			if(length(displayDiv.med_TRQTY.Value) < 1 || displayDiv.med_TRQTY.Value == "0.00" || displayDiv.med_TRQTY.Value == "0"){
				alert("수량확인");
				return;
			}
			if(confirm("저장하시겠습니까? ") == false) return;
			fn_Save();
			ed_Action.SetFocus();//fn_Input();
		}
	}
}

function ed_JPNNO_OnChanged(obj,strText)
{
	ds_BUUZ160.ClearData();
	ed_CONNO.Text = "";
	st_VNAME.Text = "";
	ca_TRNDT.Value = "";
	ed_HOUSE.Text = "";
}
