function on_LoadCompleted(obj){
	getTitle();	
	getComCode();
	//getMst040();
	//Div0.st_beforYM.Text = substr(ds_MST040PF.GetColumn(0,"MJINT"),0,4)+"-"+substr(ds_MST040PF.GetColumn(0,"MJINT"),4,2);
	gFcOnTopButtonColor("0"); //상단 버튼 활성화 여부  1:조회,excel                1.1:조회,발행,excel                 1.2:조회     
	                          //                       2.1:조회,추가,삭제,저장     2.2:조회,추가,삭제,저장,excel       2.3:조회,저장,excel
	                          //                       2.4:조회,추가,저장          2.5:저장                            2.6:조회,삭제,저장
	                          //                       2.7:조회,추가,저장,excel 		

	//st_Msg.Visible = false;
	Div0.cal_PLNYM.SetFocus();
}

function getComCode(){

	http.Sync=true;
	var sKind		= "getComCode";						
	var sMethodName = "service::login/selectComCode.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ComCode_PLNT=ds_ComCode_PLNT";
	var sArgument   = "";
		sArgument   += " CLASS='PLNT'";
		sArgument   += " CODE1=" + quote(company);		

	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync=false;

	Div0.cb_searchCodeName1.Value=comps;

}

function getMst040()
{
	var sKind		= "getMST040PF";						
	var sMethodName = "service::pscomlib/selectMST040.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_MST040PF=ds_MST040PF";   					// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
	sArgument += " LIB2=" + quote(Lib2);
	sArgument += " COMPS=" + quote(Div0.cb_searchCodeName1.Value);
	sArgument += " MAJCD=" + quote("BZZE220CP");
	http.Sync 		= true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync 		= false;
	next_month = AddMonth(ds_MST040PF.GetColumn(0,"MJINT")+"01",1);
}
function setMst040_1()
{
	var sKind		= "setmst040_1";						
	var sMethodName = "service::pscomlib/updateMST040.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "";    		 								// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
	sArgument += " LIB2=" + quote(Lib2);
	sArgument += " COMPS=" + quote(Div0.cb_searchCodeName1.Value);
	sArgument += " MAJCD=" + quote("BZZE220CP");
	sArgument += " MJINT=" + quote(substr(Div0.cal_PLNYM.Value,0,6));
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
}

/********************************************************************** 
 * 내용설명 : 마감
 * 주요변수 : 
 **********************************************************************/ 
function setBZZE220C(){
	if(length(trim(Div0.cb_searchCodeName1.Value)) < 1){
		alert(setPopMessage("1006"));  //[알림] 지원하지 않는 시스템 입니다. 정보관리팀으로 문의하십시요.
		setMessage("1006");	
		return;
	}
	if(length(trim(Div0.cal_PLNYM.Value)) < 8){
		alert(setPopMessage("4020"));  //[확인] 마감년월을 확인바랍니다.
		setMessage("4020");	
		Div0.cal_PLNYM.SetFocus();
		return;
	}

	if(confirm(substr(Div0.cal_PLNYM.Value,0,4)+"년 "+substr(Div0.cal_PLNYM.Value,4,2)+"월분류별 집행ITEM 및 업체수 생성 작업입니다. 작업하시겠습니까? ") == false) return;

	st_Msg.Visible = true;
	Div0.Enable = false;
	SetWaitCursor(true);
	SetCapture();

	var sKind		= "setBZZE220C";
	var sMethodName = "service::psimmlib/saveBZZE220C.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "";    										// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
	sArgument += " LIB1=" + quote(Lib1);
	sArgument += " LIB2=" + quote(Lib2);
	sArgument += " COMPS=" + quote(Div0.cb_searchCodeName1.Value);
	sArgument += " YYMM=" + quote(substr(Div0.cal_PLNYM.Value,0,6));
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
}

/********************************************************************** 
 * 내용설명 : Transaction 리턴후 실행 함수
 * 주요변수 : 
 * 			- svcId	Transaction 고유 ID
 * 			- rtnCd 	에러코드
 * 			- rtnMsg 		에러 메세지
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("1002")); //[경고] 처리 중 오류가 발생하였습니다.
		setMessage("1002");
	}else{
		ReleaseCapture();
		SetWaitCursor(false);
		st_Msg.Visible = false;
		alert(setPopMessage("1001")); //[알림] 정상적으로 처리가 완료되었습니다.
		setMessage("1001");
	}
}

