/********************************************************************** 
 * 내용설명 : 화면조회
 * 주요변수 : 
 **********************************************************************/ 
function getBZZE401Q(){
	if(ed_PRCGUB.Text != '1' && ed_PRCGUB.Text != '2' && ed_PRCGUB.Text != '3'){
		alert(setPopMessage("5013"));  //[확인] 적용단가를 확인바랍니다.
		setMessage("5013");	
		cal_IMHYM.SetFocus();
		return;
	}
	if(length(trim(cal_IMHYM.Value)) < 8){
		alert(setPopMessage("2528"));  //[[확인] 마감년월을 확인바랍니다.
		setMessage("2528");	
		cal_IMHYM.SetFocus();
		return;
	}
	if(length(trim(cmb_CLSGU.Value)) < 1){
		alert(setPopMessage("2033"));  //[알림] 구분 코드를 입력해 주세요.
		setMessage("2033");	
		cmb_CLSGU.SetFocus();
		return;
	}
	/* 삭제 : 2019.12.02 NEWDIONE로 SSO관리전환
	//as400 USERID Get
	http.Sync = true;
	var sKind		= "getSsoAccount";						
	var sMethodName = "service::user/selectSsoAccount.do";   	// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             			// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_ssoaccount=ds_ssoaccount";    		// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
	sArgument += " USER_ID="    + quote(G_ds_user.GetColumn(0,"USER_ID"));
	sArgument += " INOUT_GUBN=" + quote("I");	
	sArgument += " COMPANY="    + quote(company);
	sArgument += " SYSTEM_SEQ=" + quote("4");
	http.Sync = true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync = false;
	if (ds_ssoaccount.GetRowCount() < 1){
		alert(setPopMessage("1014"));
		setMessage("1014");	
		return;
	}
	if (length(trim(ds_ssoaccount.GetColumn(0,"SYSTEM_ID"))) < 1){
		alert(setPopMessage("1014"));
		setMessage("1014");	
		return;
	}
	*/
	//callUser = ds_ssoaccount.GetColumn(0,"SYSTEM_ID");
	//callPass = ds_ssoaccount.GetColumn(0,"SYSTEM_PW");

	//사업부별 공통ID(as400 USERID)로 실행
	var iDget = getRemotecallID(comps);
	if (iDget != "ERROR"){
		callUser = iDget;
		callPass = iDget;
	}else{
		alert(setPopMessage("1444"));
		setMessage("1444");	
		return;
	}

	//리모트콜 실행(BHZZ401RC)
	//param = cb_searchCodeName1.Value + substr(cal_IMHYM.Value,0,6) + cmb_CLSGU.Value + ed_PRCGUB.Text+ds_ssoaccount.GetColumn(0,"SYSTEM_ID");
	param = cb_searchCodeName1.Value + substr(cal_IMHYM.Value,0,6) + cmb_CLSGU.Value + ed_PRCGUB.Text+"R"+ trim(G_ds_user.GetColumn(0,"USER_ID"));	//2020.08.19 GCI-파라미터 변경으로 수정
		param += " ";
	}
	param += callUser;		
	for(j=1;j<=10-length(callUser);j++){
		param += " ";
	}
	param += '000';

	SetWaitCursor(true);
	SetCapture();

	WTA_RemoteCall0.RC_SetAttrValue(0,"2984",exeTime);	

	// 연결 (접속아이피 , 아이디, 비밀번호)	
	var rtn = WTA_RemoteCall0.RC_Connect(callIp,callUser,callPass);
	//접속성공시 
	if(rtn != 0){
		//프로그램 Call (라이브러리명, 프로그램명, 인자값, 크기);
		var rtn2 = WTA_RemoteCall0.RC_RmtPgmCall(pgmLib,pgmName,param,pgmSize);
		//정상적으로 수행했을 시
		if(rtn2 != 0){
			if (substr(WTA_RemoteCall0.RC_GetReturnParm(1),50,3) == "999") {
				WTA_RemoteCall0.RC_Disconnect();	//연결종료
				ReleaseCapture();
				SetWaitCursor(false);
				var sKind		= "getBZZE401Q";						
				var sMethodName = "service::psimmlib/selectBZZE401Q.do";     		// 서버에서 호출 될 Method 명
				var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
				var sOutDataSet = "ds_BZZE401Q=ds_BZZE401Q";    				// 조회 후 돌려 받을 DataSet 명칭
				var sArgument   = "";

				sArgument += "@LIB1=" + quote(Lib1);
				sArgument += "@LIB2=" + quote(Lib2);
				sArgument += "@PRCGUB=" + quote(ed_PRCGUB.Text);
				sArgument += "@GUB=" + quote(cmb_CLSGU.Value);
				//sArgument += "@IMSIPF=" + quote(ds_ssoaccount.GetColumn(0,"SYSTEM_ID"));
				sArgument += "@IMSIPF=" + quote("R"+trim(G_ds_user.GetColumn(0,"USER_ID"))); //2020.08.19 GCI-파라미터 변경으로 수정
				var str = dialog("Common::PGMLOAD_IMM.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");	
				//Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
				if (str == 'T'){  //정상조회
					alert(setPopMessage("1004"));
					setMessage("1004");	
				}else if (str == 'B'){   //data값이 없을때
					alert(setPopMessage("1003"));
					setMessage("1003");	
				}else if (str == 'E'){   //error발생시
					alert(setPopMessage("1002"));
					setMessage("1002");	
				}
			}else if (substr(WTA_RemoteCall0.RC_GetReturnParm(1),50,3) == "888") {
				ReleaseCapture();
				SetWaitCursor(false);
				WTA_RemoteCall0.RC_Disconnect();	//연결종료
				var ruser = trim(substr(WTA_RemoteCall0.RC_GetReturnParm(1),50,10));
				var rcount = toNumber(substr(WTA_RemoteCall0.RC_GetReturnParm(1),60,2))-1;
				alert("사용자("+ruser+")외 "+rcount+"명이 사용중입니다. 작업이 불가능 합니다.");
				return false;
			}else if (substr(WTA_RemoteCall0.RC_GetReturnParm(1),50,3) == "777") {
				ReleaseCapture();
				SetWaitCursor(false);
				WTA_RemoteCall0.RC_Disconnect();	//연결종료
				alert(setPopMessage("1002")); //[경고] 처리 중 오류가 발생하였습니다.
				setMessage("1002");	
				return false;
			}else {
				ReleaseCapture();
				SetWaitCursor(false);
				WTA_RemoteCall0.RC_Disconnect();	//연결종료
				alert(setPopMessage("1015")); //[경고] AS400 CL 처리 중 오류가 발생하였습니다.
				setMessage("1015");	
				return false;
			}
		}else{
			ReleaseCapture();
			SetWaitCursor(false);
			var bstrErrorMessage = WTA_RemoteCall0.bstrErrorMessage;
			WTA_RemoteCall0.RC_Disconnect();	//연결종료
			//오류 코드 경고창
			alert("에러 메세지=======>" + bstrErrorMessage);
		}
	//접속실패시
	}else{
		ReleaseCapture();
		SetWaitCursor(false);
		var ulErrorCode = WTA_RemoteCall0.ulErrorCode;
		WTA_RemoteCall0.RC_Disconnect();	//연결종료
		//오류 코드 경고창
		alert("접속 실패 오류=======>" + ulErrorCode);
	}
}


/********************************************************************** 
 * 내용설명 : 엑셀파일로 저장
 * 주요변수 : 
 **********************************************************************/ 
function setExcel()
{
	var fName   = "외주부품 소요량 산출금액 조회";
	var arrSheets  =  Array();  

	arrSheets[0]  = "MainGrid|외주부품 소요량 산출금액 조회";
	createExcelSheetS2(FileDialog0, fName ,arrSheets);

}


/********************************************************************** 
 * 내용설명 : Transaction 리턴후 실행 함수
 * 주요변수 : 
 * 			- sMethodName	Transaction 고유 ID
 * 			- ErrorCode 	에러코드
 * 			- ErrorMSG 		에러 메세지
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");

	}else if(svcId=="getBZZE401Q"){
		// 조회건수가 없을경우
		if(ds_BZZE401Q.rowcount <= 0){
			alert(setPopMessage("1003"));
			setMessage("1003");	
		// 정상 조회
		} else {
			alert(setPopMessage("1004"));
			setMessage("1004");				
		}	
	}
}

function Grid0_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
	if(nCell <= 8) lib_grid_SetGridSort(obj,nCell);
}

function Grid0_OnKeyDown(obj,nChar,bShift,bCtrl,bAlt,nLLParam,nHLParam)
{
	// Ctrl + C
	if(bCtrl == 1 && nChar == 67) {

		// grd_Standard.AreaSelect = true;
		grd_fn_ClipboardCopy(obj, strSeparator);

	}
}

