function getMSTE100(){

	var result = true;

	if(!validationChk()){
		return;
	}		

	var sKind		= "getMSTE100";						
	var sMethodName = "service::psmstlib/selectMSTE100.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_MSTE100=ds_MSTE100";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
	sArgument += "@LIB1=" + quote(Lib1);
	sArgument += "@LIB2=" + quote(Lib2);
	sArgument += "@COMPS=" + quote(ComboBoxPlant.Value); //사업부
	sArgument += "@PSPNO=" + quote(ed_PSPNO.Text); //업체

	var str = dialog("Common::PGMLOAD_SAL.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");

	if (str == 'T'){  //정상조회
		alert(setPopMessage("1004"));
		setMessage("1004");	
	}else if (str == 'B'){   //data값이 없을때
		alert(setPopMessage("1003"));  //[알림] 신규입력입니다.
		setMessage("1003");	
	}else if (str == 'E'){   //error발생시
		alert(setPopMessage("1002"));
		setMessage("1002");	
	}
	return result;
}

function setMSTE100(){

	var sKind		= "setMSTE100";						
	var sMethodName = "service::psmstlib/insertMSTE100.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "ds_MSTE100=ds_MSTE100:U";               //자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_MSTE100_CHECK=ds_MSTE100_CHECK";  	   // 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
		sArgument += " LIB2=" + quote(Lib2);
		sArgument += " SCOMPS=" + quote(ComboBoxPlant.Value); //사용자
		sArgument += " SPRTNO=" + quote(ed_PSPNO.Text); //사용자
		sArgument += " SUSER=" + quote("PC"+userId); //사용자

	http.Sync=true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
	http.Sync=false;

}

/********************************************************************** 
 * 내용설명 : Transaction 리턴후 실행 함수
 * 주요변수 : 
 * 			- sMethodName	Transaction 고유 ID
 * 			- ErrorCode 	에러코드
 * 			- ErrorMSG 		에러 메세지
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	if (ds_MSTE100_CHECK.GetRowCount() > 0){
		for(var i=0;i<ds_MSTE100_CHECK.GetRowCount();i++){
			alert("CODE : " +ds_MSTE100_CHECK.GetColumn(i,"PRTNO") + " = " + ds_MSTE100_CHECK.GetColumn(i,"RTMSG"));
		}
		getMSTE100();  //error항목이 존재시에는 재조회
		return;
	}
	// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");

	}else 
	{
		//getSalz101();  //error항목이 존재시에는 재조회
		alert(setPopMessage("1001"));
		setMessage("1001");	
	}
}
/********************************************************************** 
 * 내용설명 : validation 체크
 **********************************************************************/ 

function validationChk(){

	var result = true;
	var pspnoVal = ed_PSPNO.Text;
		ed_PSPNO.BKColor="white";

	if (pspnoVal == "")
	{
		ed_PSPNO.BKColor="paleturquoise";
		alert(setPopMessage("2017"));
		result = false;
	}
	return result;
}
