function imgPspno_OnClick(obj,nX,nY)
{
	var sTemp=trim(ed_pspno.Text);
	if (length(trim(ed_pspno.Text)) == 0) sTemp="zzzzzzzzzz";
	var selectnumber = "A1";
	var rtn = dialog("Common::commonPopup.xml","SEL=" + selectnumber + " " + "CODE=" + sTemp,584,528);
	if (length(rtn) != 0){
		ed_pspno.Text = rtn[0];
		st_pspno.Text = rtn[1];
	}
}

function MST0050PF_OnKillFocus(obj)
{
	if(length(trim(obj.Text)) < 1) return;
	//품번Check
	var grpVal = "";
	var grpChk = substr(obj.ID,3,5);
	if( grpChk == "ROLCD"){
		grpVal="ROL";
	}
	else if( grpChk == "RGNCD"){
		grpVal="REG";
	}
	else if( grpChk == "CARCD"){
		grpVal="CAR";
	}
	else if( grpChk == "COLOR"){
		grpVal="COL";
	}			
	http.usewaitcursor = false;
	http.Sync 	= true;
	var sKind		= "getMST050";		
	var sMethodName = "service::pscomlib/selectMST050PF.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_MST050PF=ds_MST050PF";
	var sArgument   = "";
		sArgument  += " LIB2=" + quote(Lib2);
		sArgument  += " COMPS=" + quote(ComboBoxPlant.Value);
		sArgument  += " GRPCD=" + quote(grpVal);
		sArgument  += " MSTCD=" + quote(obj.Text);
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	if (ds_MST050PF.GetRowCount() < 1){
		alert(setPopMessage("4023"));
		setMessage("4023");	
		obj.SetFocus();
		return;
	}

	if( grpChk == "ROLCD"){
		st_ROLCD.Text=ds_MST050PF.GetColumn(0,"MSTRM");	
	}
	else if( grpChk == "RGNCD"){
		st_RGNCD.Text=ds_MST050PF.GetColumn(0,"MSTRM");	
	}
	else if( grpChk == "CARCD"){
		st_CARCD.Text=ds_MST050PF.GetColumn(0,"MSTRM");	
	}	
	else if( grpChk == "COLOR"){
		st_COLOR.Text=ds_MST050PF.GetColumn(0,"MSTRM");	
	}				
	//st_ROLCD.Text=ds_MST050PF.GetColumn(0,"MSTRM");

}

