function ed_MKALC_OnKillFocus(obj)
{
	if(length(trim(obj.Text)) < 1) return;

	http.usewaitcursor = false;
	http.Sync 	= true;
	var sKind		= "getSVM100PF";		
	var sMethodName = "service::psmstlib/selectSVM100PF.do";
	var sInDataSet  = "";
	var sOutDataSet = "ds_SVM100PF=ds_SVM100PF";
	var sArgument   = "";
		sArgument  += " LIB2=" + quote(Lib2);
		sArgument  += " COMPS=" + quote(ComboBoxPlant.Value);
		sArgument  += " PSPNO=" + quote(ed_PSPNO.Text);
		sArgument  += " MKALC=" + quote(ed_MKALC.Text);
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	if (ds_SVM100PF.GetRowCount() < 1){
		alert(setPopMessage("4026"));
		setMessage("4026");	
		obj.SetFocus();
		return;
	}	
}

