var strSeparator = "	";
var Lib1 = G_ds_user.GetColumn(0,"LIB1"); // cigma library(pereslib,psreslib)
var Lib2 = G_ds_user.GetColumn(0,"LIB2"); // 회사 library(psedblib,pscdblib)
var Lib3 = G_ds_user.GetColumn(0,"LIB3"); // 사업부 library(psedblib,pscdblib,pshdblib,pssdblib)
var company = G_ds_user.GetColumn(0,"COMPANY");
var comps = G_ds_user.GetColumn(0,"PLANT_CD");

function on_LoadCompleted(obj){
	getTitle();	
	getDesign();
	gFcOnTopButtonColor("2.2"); //조회, 추가, 저장
	getComCode(); //공장구분 선택
}

function getComCode(){
	var sKind		= "getComCode";						
	var sMethodName = "service::login/selectComCode.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ComCode_PLNT=ds_ComCode_PLNT";
	var sArgument   = "";
		sArgument   += " CLASS='PLNT'";
		sArgument   += " CODE1=" + quote(company);		
	http.Sync=true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync=false;
	ComboBoxPlant.Value=comps;
	//ComboBoxPlant.Enable = false; 
}

function getDesign(){
	//MainGrid.ToolTipText = setPopMessage("2053");
	MainGrid.BkColor = G_ds_design.GetColumn(0,"BkColor");
	MainGrid.BkColor2 = G_ds_design.GetColumn(0,"BkColor2");
	MainGrid.BkSelColor = G_ds_design.GetColumn(0,"BkSelColor");
	MainGrid.Face3dColor = G_ds_design.GetColumn(0,"Face3dColor");
	MainGrid.BoldHead = G_ds_design.GetColumn(0,"BoldHead");
	MainGrid.HeadHeight = G_ds_design.GetColumn(0,"HeadHeight");
	inputBar.Width = G_ds_design.GetColumn(0,"Width");
	//MainGrid.Width = G_ds_design.GetColumn(0,"Width");

	searchBar.Width = G_ds_design.GetColumn(0,"Width");
	searchBar.Top = G_ds_design.GetColumn(0,"SBTop");	
}

function getMSTZ060(){

	if(!validationChk()){
		return;
	}

	var sKind		= "getMSTZ060";						
	var sMethodName = "service::psmstlib/selectMSTZ060.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_MSTZ060=ds_MSTZ060";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
		//sArgument += "@LIB1=" + quote(Lib1);
		sArgument += "@LIB2=" + quote(Lib2);
		sArgument += "@COMPS=" + quote(ComboBoxPlant.Value); //사업부
		sArgument += "@PROCS=" + quote(ed_PROCS.Text); //차종코드
	var str = dialog("Common::PGMLOAD_MST.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");
	if (str == 'T'){  //정상조회
		alert(setPopMessage("1004"));
		setMessage("1004");
	}else if (str == 'B'){   //data값이 없을때
		alert(setPopMessage("1003"));
		setMessage("1003");	
	}else if (str == 'E'){   //error발생시
		alert(setPopMessage("1002"));
		setMessage("1002");	
	}
}

/********************************************************************** 
 * 내용설명 : insert, update, delete
 **********************************************************************/ 

 //등록시 업체코드 존재유무 체크함. 없으면 동록불가.
function setMSTZ060(){

	if(length(trim(ed_PROCS.Text)) < 1){
		alert(setPopMessage("2026"));  //[알림] 공정코드를 입력하세요.
		setMessage("2026");	
		ed_PROCS.SetFocus();
		return;
	}	
	if(length(trim(ed_PROSM.Text)) < 1){
		alert(setPopMessage("6001"));  //[알림]공정약명을 입력하세요.
		setMessage("6001");	
		ed_PROSM.SetFocus();
		return;
	}
	if(length(trim(ed_PRONM.Text)) < 1){
		alert(setPopMessage("6002"));  //[알림]공정원명을 입력하세요.
		setMessage("6002");	
		ed_PRONM.SetFocus();
		return;
	}
	var sKind		= "setMSTZ060";						
	var sMethodName = "service::psmstlib/saveMSTZ060.do";    // 서버에서 호출 될 Method 명
	var sInDataSet  = "ds_MSTZ060=ds_MSTZ060:U";               //자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_MSTZ060_CHECK=ds_MSTZ060_CHECK";  	   // 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
		sArgument += " LIB2=" + quote(Lib2);
	http.Sync=true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
	http.Sync=false;

}
/********************************************************************** 
 * 내용설명 : Transaction 리턴후 실행 함수
 * 주요변수 : 
 * 			- sMethodName	Transaction 고유 ID
 * 			- ErrorCode 	에러코드
 * 			- ErrorMSG 		에러 메세지
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	if (ds_MSTZ060_CHECK.GetRowCount() > 0){
		for(var i=0;i<ds_MSTZ060_CHECK.GetRowCount();i++){
			alert("CODE : " +ds_MSTZ060_CHECK.GetColumn(i,"PROCS") + " = " + ds_MSTZ060_CHECK.GetColumn(i,"RTMSG"));
		}
		getMSTZ060();  //error항목이 존재시에는 재조회
		return;
	}
	// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");

	}else 
	{
		//getSalz101();  //error항목이 존재시에는 재조회
		alert(setPopMessage("1001"));
		setMessage("1001");	
	}
}

/********************************************************************** 
 * 내용설명 : validation 체크
 **********************************************************************/ 

function validationChk(){

	var result = true;
	var procs = ed_PROCS.Text;
		ed_PROCS.BKColor="white";

	if (procs == "")
	{
		ed_PROCS.BKColor="paleturquoise";
		//alert("[알림] 공정코드를 입력하세요.");
		alert(setPopMessage("2026"));//[알림] 공정코드를 입력하세요.
		setMessage("2026");	
		result = false;
	}	
	return result;
}

