function setExcel()
{
	var fName   = "작업공정마스타등록";
	var arrSheets  =  Array();  

	arrSheets[0]  = "MainGrid|작업공정마스타등록";
	createExcelSheetS2(FileDialog0, fName ,arrSheets);

}

