function fn_Input()
{
	if(!validationChk()){
		return;
	}
	if(ds_MSTZ060.rowcount > 0 ){
		//alert("[알림]등록은 1건만 가능합니다.");
		alert(setPopMessage("4747"));//[알림]등록은 1건만 가능합니다.
		setMessage("4747");
		return;
	}
	else{
		var sKind		= "getMSTZ060";						
		var sMethodName = "service::psmstlib/selectMSTZ060.do";     		// 서버에서 호출 될 Method 명
		var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
		var sOutDataSet = "ds_MSTZ060=ds_MSTZ060";    				// 조회 후 돌려 받을 DataSet 명칭
		var sArgument   = "";
			//sArgument += "@LIB1=" + quote(Lib1);
			sArgument += " LIB2=" + quote(Lib2);
			sArgument += " COMPS=" + quote(ComboBoxPlant.Value); //사업부
			sArgument += " PROCS=" + quote(ed_PROCS.Text); //차종코드
			http.Sync=true;
			Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
			http.Sync=false;
		if(ds_MSTZ060.rowcount>0){
			//alert("[경고] 이미 등록된 코드입니다. 다른 코드를 입력하세요!");
			ds_MSTZ060.ClearData();
			alert(setPopMessage("2533"));//[경고] 이미 등록된 코드입니다. 다른 코드를 입력하세요!
			setMessage("2533");	
			return;
		}	
		else{
			var sKind		= "getBM001PR";						
			var sMethodName = "service::pscomlib/selectBM001PR.do";    // 서버에서 호출 될 Method 명
			var sInDataSet  = "";               //자료 저장 시 보낼 DataSet 명칭
			var sOutDataSet = "ds_BM001PR=ds_BM001PR";  	   // 조회 후 돌려 받을 DataSet 명칭
			var sArgument   = "";
				sArgument += " LIB1=" + quote(Lib1);
				sArgument += " PROCS=" + quote(ed_PROCS.Text); //공정코드
			http.Sync=true;
			Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
			http.Sync=false;
			if(ds_BM001PR.rowcount<1){
				//alert("[알림] PROCESS MA에 미등록된 공정코드입니다.");
				alert(setPopMessage("4746"));//[알림] PROCESS MA에 미등록된 공정코드입니다.
				setMessage("4746");			
				return;
			}else{
				ds_MSTZ060.AddRow();
				ds_MSTZ060.SetColumn(ds_MSTZ060.currow,"COMPS",ComboBoxPlant.Value);
				ds_MSTZ060.SetColumn(ds_MSTZ060.currow,"PROCS",ed_PROCS.Text);
			}
		}		
	}
}

function fn_Delete()
{
	if(ds_MSTZ060.rowcount < 1) return;			//삭제가 없으면 건너뛰기		
	ds_MSTZ060.DeleteRow(ds_MSTZ060.currow);	//삭제		
	alert(setPopMessage("1011"));				//[알림] 화면에서만 제거되었습니다. 저장하시면 삭제됩니다.		
	setMessage("1011");
}

