function on_LoadCompleted(obj){
	getTitle();	
	getDesign();
	gFcOnTopButtonColor("2.3"); //조회, 저장기능만 사용함.(생산코드만 수정가능)
	getComCode(); //공장구분 선택
}

function getComCode(){
	var sKind		= "getComCode";						
	var sMethodName = "service::login/selectComCode.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ComCode_PLNT=ds_ComCode_PLNT";
	var sArgument   = "";
		sArgument   += " CLASS='PLNT'";
		sArgument   += " CODE1=" + quote(company);		
	http.Sync=true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync=false;
	ComboBoxPlant.Value=comps;
	//ComboBoxPlant.Enable = false; 
}

function getDesign(){
	//MainGrid.ToolTipText = setPopMessage("2053");
	MainGrid.BkColor = G_ds_design.GetColumn(0,"BkColor");
	MainGrid.BkColor2 = G_ds_design.GetColumn(0,"BkColor2");
	MainGrid.BkSelColor = G_ds_design.GetColumn(0,"BkSelColor");
	MainGrid.Face3dColor = G_ds_design.GetColumn(0,"Face3dColor");
	MainGrid.BoldHead = G_ds_design.GetColumn(0,"BoldHead");
	MainGrid.HeadHeight = G_ds_design.GetColumn(0,"HeadHeight");
	//MainGrid.Width = G_ds_design.GetColumn(0,"Width");

	searchBar.Width = G_ds_design.GetColumn(0,"Width");
	searchBar.Top = G_ds_design.GetColumn(0,"SBTop");	
}

function getMSTZ090(){

	if(!validationChk()){
		return;
	}
	//lastDay=""; //초기화
	//getLastday();

	var sKind		= "getMSTZ090";						
	var sMethodName = "service::psmstlib/selectMSTZ090.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_MSTZ090=ds_MSTZ090";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
		//sArgument += "@LIB1=" + quote(Lib1);
		sArgument += "@LIB2=" + quote(Lib2);
		sArgument += "@COMPS=" + quote(ComboBoxPlant.Value); //사업부
		sArgument += "@PRCYM=" + quote(substr(cmb_PRCYM.Value,0,6)); //달력년월
	var str = dialog("Common::PGMLOAD_MST.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");
	if (str == 'T'){  //정상조회
		alert(setPopMessage("1004"));
		setMessage("1004");
	}else if (str == 'B'){   //data값이 없을때
		alert(setPopMessage("1003"));
		setMessage("1003");	
	}else if (str == 'E'){   //error발생시
		alert(setPopMessage("1002"));
		setMessage("1002");	
	}

}

/********************************************************************** 
 * 내용설명 : insert, update, delete
 **********************************************************************/ 

 //등록시 업체코드 존재유무 체크함. 없으면 동록불가.
function setMSTZ090(){

	if(ds_MSTZ090.GetRowType(ds_MSTZ090.currow)=="update" ||ds_MSTZ090.GetRowType(ds_MSTZ090.currow)=="insert" ){
		if(!ds_MSTZ090_CanRowPosChange()){
			return;
		}
	}
	var sKind		= "setMSTZ090";						
	var sMethodName = "service::psmstlib/saveMSTZ090.do";    // 서버에서 호출 될 Method 명
	var sInDataSet  = "ds_MSTZ090=ds_MSTZ090:U";               //자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_MSTZ090_CHECK=ds_MSTZ090_CHECK";  	   // 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
		sArgument += " LIB2=" + quote(Lib2);
	http.Sync=true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
	http.Sync=false;

}
/********************************************************************** 
 * 내용설명 : Transaction 리턴후 실행 함수
 * 주요변수 : 
 * 			- sMethodName	Transaction 고유 ID
 * 			- ErrorCode 	에러코드
 * 			- ErrorMSG 		에러 메세지
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	if (ds_MSTZ090_CHECK.GetRowCount() > 0){
		for(var i=0;i<ds_MSTZ090_CHECK.GetRowCount();i++){
			alert("달력일자 : " +ds_MSTZ090_CHECK.GetColumn(i,"PRCDT") + " = " + ds_MSTZ090_CHECK.GetColumn(i,"RTMSG"));
		}
		getMSTZ090();  //error항목이 존재시에는 재조회
		return;
	}
	// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");
	}else{
		//getSalz101();  //error항목이 존재시에는 재조회
		alert(setPopMessage("1001"));
		setMessage("1001");	
	}
}

/********************************************************************** 
 * 내용설명 : validation 체크
 **********************************************************************/ 
function validationChk(){
	var result = true;
	cmb_PRCYM.BKColor="white";

	if(cmb_PRCYM.Value == ""){
		cmb_PRCYM.BKColor="paleturquoise";
		//alert("[알림]달력년월을 입력하세요.");
		alert(setPopMessage("6100"));//[알림]달력년월을 입력하세요.
		setMessage("6100");
		result = false;
	}
	return result;
}

/********************************************************************** 
 * 내용설명 : 상위메뉴 변경시 초기화
 **********************************************************************/ 
function common_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	ds_MSTZ090.ClearData();
}

/********************************************************************** 
 * 내용설명 : 엑셀파일로 저장
 **********************************************************************/ 
function setExcel()
{
	var fName   = "달력마스타수정";
	var arrSheets  =  Array();  

	arrSheets[0]  = "MainGrid|달력마스타수정";
	createExcelSheetS2(FileDialog0, fName ,arrSheets);

}

/********************************************************************** 
 * 내용설명 : Sort
 **********************************************************************/ 
function MainGrid_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
	if(nCell != -1) lib_grid_SetGridSort(obj,nCell);
}

