/*미사용
//마지막일계산
function getLastday(){

	var year = parseInt(substr(cmb_PRCYM.Value,0,4));
	var month = substr(cmb_PRCYM.Value,4,2);
	var end_day = "";

	if (month=="01" || month=="03" || month=="05" || month=="07" || month=="08" || month=="10" || month=="12") {
		end_day = "31";
	}else if (month=="04" || month=="06" || month=="09" || month=="11") {
		end_day = "30";
	}else if (month=="02" && year%4 == 0) {
		end_day = "29";
	}else if (month=="02" && year%4 != 0) {
		end_day = "28";
	}
	lastDay=end_day;
}
