function ds_MSTZ090_CanRowPosChange(obj,nOldRow,nRow)
{
	http.usewaitcursor = false;
	var p_PRCDD=ds_MSTZ090.GetColumn(ds_MSTZ090.currow,"PRCDD"); //달력일자
	var p_PRCMK=ds_MSTZ090.GetColumn(ds_MSTZ090.currow,"PRCMK"); //생산코드

	if(ds_MSTZ090.GetRowType(ds_MSTZ090.currow)=="update" ||ds_MSTZ090.GetRowType(ds_MSTZ090.currow)=="insert" ){
		if(length(p_PRCDD) !=0){
			if(length(p_PRCMK) == 0){
				//alert("[알림]생산코드를 입력하세요.");
				alert(setPopMessage("6101")); //[알림]생산코드를 입력하세요.
				setMessage("6101");	
				return false;
			}
		}
		return true;
	}
	http.usewaitcursor = true;
}
