}

function getMSTZ111Q(){
	if(length(trim(ca_DTA.Value)) != 0){
		if(length(trim(ca_DTA.Value)) != 8){
			alert(setPopMessage("1502"));  //[확인] 거래처코드 확인바랍니다.
			setMessage("1502");	
			ca_DTA.SetFocus();
			return;
		}
	}



	//as400 USERID Get
	http.Sync = true;
	var sKind		= "getSsoAccount";						
	var sMethodName = "service::user/selectSsoAccount.do";   	// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             			// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_ssoaccount=ds_ssoaccount";    		// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
	sArgument += " USER_ID="    + quote(G_ds_user.GetColumn(0,"USER_ID"));
	sArgument += " INOUT_GUBN=" + quote("I");	
	sArgument += " COMPANY="    + quote(company);
	sArgument += " SYSTEM_SEQ=" + quote("4");
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	if (ds_ssoaccount.GetRowCount() < 1){
		alert(setPopMessage("1014")); //  [경고] Display Session User ID가 존재하지 않습니다. 정보관리팀에 문의바랍니다.
		setMessage("1014");	
		return;
	}
	//callUser = ds_ssoaccount.GetColumn(0,"SYSTEM_ID");
	//callPass = ds_ssoaccount.GetColumn(0,"SYSTEM_PW");
	//사업부별 공통ID(as400 USERID)로 실행

	var iDget = getRemotecallID(comps);
	if (iDget != "ERROR"){
		callUser = iDget;
		callPass = iDget;
	}else{
		alert(setPopMessage("1444"));
		setMessage("1444");	
		return;
	}
	/*
	var pna = right("     "+ed_PNA.Text,5);
	var pnb = right("     "+ed_PNB.Text,5);
	var ita = right("               "+ed_ITA.Text,15);
	var itb = right("               "+ed_ITB.Text,15);
	var cla = right(" "+ed_CLA.Text,1);
	var clb = right(" "+ed_CLB.Text,1);
	var psa = right("     "+ed_PSA.Text,5);
	var psb = right("     "+ed_PSB.Text,5);
	var usa = right(" "+ed_USA.Text,1);
	var usb = right(" "+ed_USB.Text,1);
	var dta = right("      "+ca_DTA.Value,6);
	var dtb = right("      "+ca_DTB.Value,6);
	var gub = cmb_gubun.Value;
	*/
	var pna = left(ed_PNA.Text+"     ",5);
	var pnb = left(ed_PNB.Text+"     ",5);
	var ita = left(ed_ITA.Text+"               ",15);
	var itb = left(ed_ITB.Text+"               ",15);
	var cla = left(ed_CLA.Text+" ",1);
	var clb = left(ed_CLB.Text+" ",1);
	var psa = left(ed_PSA.Text+"     ",5);
	var psb = left(ed_PSB.Text+"     ",5);
	var usa = left(ed_USA.Text+" ",1);
	var usb = left(ed_USB.Text+" ",1);
	var dta = left(ca_DTA.Value+"      ",6);
	var dtb = left(ca_DTB.Value+"      ",6);
	var gub = cmb_gubun.Value;
	//리모트콜 실행
	param = ComboBoxPlant.Value + pna + pnb + ita + itb + cla + clb + psa + psb + usa + usb + dta + dtb + gub;
	param += callUser;		
	for(j=1;j<=10-length(callUser);j++){
		param += " ";
	}
	param += '000';
	ed_param.Text = param;
	SetWaitCursor(true);
	SetCapture();

	WTA_RemoteCall0.RC_SetAttrValue(0,"2984",exeTime);	

	// 연결 (접속아이피 , 아이디, 비밀번호)	
	var rtn = WTA_RemoteCall0.RC_Connect(callIp,callUser,callPass);

	//접속성공시 
	if(rtn != 0){
		//프로그램 Call (라이브러리명, 프로그램명, 인자값, 크기);
		var rtn2 = WTA_RemoteCall0.RC_RmtPgmCall(pgmLib,pgmName,param,pgmSize);
		//정상적으로 수행했을 시
		if(rtn2 != 0){
			ReleaseCapture();
			SetWaitCursor(false);
			if (substr(WTA_RemoteCall0.RC_GetReturnParm(1),79,3) == "999") {
				WTA_RemoteCall0.RC_Disconnect();	//연결종료

				gridFormatChange();

				var sKind		= "getMSTZ111Q";						
				var sMethodName = "service::psmstlib/selectMSTZ111Q.do";     		// 서버에서 호출 될 Method 명
				var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
				var sOutDataSet = "ds_MSTZ111Q=ds_MSTZ111Q";    				// 조회 후 돌려 받을 DataSet 명칭
				var sArgument   = "";
				sArgument += "@LIB1=" + quote(Lib1);
				sArgument += "@LIB2=" + quote(Lib2);
				sArgument += "@COMPS=" + quote(ComboBoxPlant.Value); //사업부
				sArgument += "@GUBUN=" + quote(cmb_gubun.Value);
				var str = dialog("Common::PGMLOAD.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");
				if (str == 'T'){  //정상조회
					alert(setPopMessage("1004"));
					setMessage("1004");	
				}else if (str == 'B'){   //data값이 없을때
					alert(setPopMessage("1003"));
					setMessage("1003");	
				}else if (str == 'E'){   //error발생시
					alert(setPopMessage("1002"));
					setMessage("1002");	
				}
			}else if (substr(WTA_RemoteCall0.RC_GetReturnParm(1),79,3) == "888") {
				alert(WTA_RemoteCall0.RC_GetReturnParm(1));
				WTA_RemoteCall0.RC_Disconnect();	//연결종료
				alert(setPopMessage("1002")); //[경고] 처리 중 오류가 발생하였습니다.
				setMessage("1002");	
				return;
			}else if (substr(WTA_RemoteCall0.RC_GetReturnParm(1),79,3) == "777") {
				alert(WTA_RemoteCall0.RC_GetReturnParm(1));
				WTA_RemoteCall0.RC_Disconnect();	//연결종료
				alert(setPopMessage("1002")); //[경고] 처리 중 오류가 발생하였습니다.
				setMessage("1002");	
				return;
			}else {
				alert(WTA_RemoteCall0.RC_GetReturnParm(1));
				WTA_RemoteCall0.RC_Disconnect();	//연결종료
				alert(setPopMessage("1015")); //[경고] AS400 CL 처리 중 오류가 발생하였습니다.
				setMessage("1015");	
				return;
			}
		}else{
			ReleaseCapture();
			SetWaitCursor(false);
			var bstrErrorMessage = WTA_RemoteCall0.bstrErrorMessage;
			WTA_RemoteCall0.RC_Disconnect();	//연결종료
			//오류 코드 경고창
			alert("에러 메세지=======>" + bstrErrorMessage);
			return;
		}
	//접속실패시
	}else{
		var ulErrorCode = WTA_RemoteCall0.ulErrorCode;
		WTA_RemoteCall0.RC_Disconnect();	//연결종료
		//오류 코드 경고창
		alert("접속 실패 오류=======>" + ulErrorCode);
		return;
	}
}

