{
	ds_MSTZ302.ClearData();
}

/********************************************************************** 
 * 내용설명 : 엑셀파일로 저장
 **********************************************************************/ 
function setExcel()
{
	var fName   = "PDM-CIGMA품번 CHECK LIST";
	var arrSheets  =  Array();  

	arrSheets[0]  = "MainGrid|PDM-CIGMA품번 CHECK LIST";
	createExcelSheetS2(FileDialog0, fName ,arrSheets);

}

