/********************************************************************** 
 * 내용설명 :  Start TAB  (사전작업)
 * 주요변수 : 
 * 			- sMethodName	Transaction 고유 ID
 **********************************************************************/ 
function Tab0_tab1_Button0_OnClick(obj)
{
	var ym = substr(ca_YYMM.Value,0,6);
	var ymd = substr(ca_YYMM.Value,0,6)+'31';
	var sKind		= "getOCAZ200C_tab1";
	var sMethodName = "service::psmstlib/selectOCAZ200C_tab1.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_OCAZ200C_tab1=ds_OCAZ200C_tab1";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
	sArgument += " LIB2=" + quote(Lib2);
	sArgument += " JOBYM=" + quote(ym);
	sArgument += " JOBYMD=" + quote(ymd);
	http.TimeOut = 500;
	http.Sync = true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
	http.TimeOut = 300;
	http.Sync = false;
}

