function cvtFile_Spool(c_user,c_pass,p_TODBF,p_TOLIB,p_JOBNAM,p_JOBUSR,p_JOBNUM,p_SPLNBR,P_pgmName)
{
	//as400 USERID 실행
	callUser = c_user;
	callPass = c_pass;

	param = cb_searchCodeName1.Value + right(cb_searchCodeName1.Value,1) +              //  right(cb_searchCodeName1.Value,1) 은 상관없음. 사용하지않음. 
			left(p_TOLIB+space(10),10) +
			left(p_TODBF+space(10),10) +
			left(p_JOBNAM+space(10),10) +
			left(p_JOBUSR+space(10),10) +
			left(p_JOBNUM+space(6),6) +
			left(p_SPLNBR+space(6),6);
	for(i=length(param)+1;i<=60;i++){
		param += " ";
	}
	param += callUser;
	for(j=1;j<=10-length(callUser);j++){
		param += " ";
	}
	param += "000";

	SetWaitCursor(true);
	SetCapture();

	WTA_RemoteCall0.RC_SetAttrValue(0,"2984",exeTime);

	// 연결 (접속아이피 , 아이디, 비밀번호)	
	var rtn = WTA_RemoteCall0.RC_Connect(callIp,callUser,callPass);

	//접속성공시 
	if(rtn != 0){
		//프로그램 Call (라이브러리명, 프로그램명, 인자값, 크기);
		var rtn2 = WTA_RemoteCall0.RC_RmtPgmCall(pgmLib,P_pgmName,param,pgmSize);
		//정상적으로 수행했을 시
		if(rtn2 != 0){
			ReleaseCapture();
			SetWaitCursor(false);
			switch (substr(WTA_RemoteCall0.RC_GetReturnParm(1),70,3)){
			case "999" :  
				WTA_RemoteCall0.RC_Disconnect();	//연결종료
				//alert(setPopMessage("1001")); //  정상적으로 처리가 완료되었습니다.	
				//setMessage("1001");
				return true;
			case "888" :  
				alert(WTA_RemoteCall0.RC_GetReturnParm(1));
				alert("NPRINT not found : 사용자SPOOL이 존재하지 않습니다.");
				WTA_RemoteCall0.RC_Disconnect();	//연결종료
				return false;
			case "777" :  
				alert(WTA_RemoteCall0.RC_GetReturnParm(1));
				WTA_RemoteCall0.RC_Disconnect();	//연결종료
				alert(setPopMessage("1002")); //[경고] 처리 중 오류가 발생하였습니다.
				setMessage("1002");	
				return false;
			case "000" :  
				alert(WTA_RemoteCall0.RC_GetReturnParm(1));
				WTA_RemoteCall0.RC_Disconnect();	//연결종료
				alert(setPopMessage("1002")); //[경고] 처리 중 오류가 발생하였습니다.
				setMessage("1002");	
				return false;
			default :
				alert(WTA_RemoteCall0.RC_GetReturnParm(1));
				WTA_RemoteCall0.RC_Disconnect();	//연결종료
				alert(setPopMessage("1015")); //[경고] AS400 CL 처리 중 오류가 발생하였습니다.
				setMessage("1015");	
				return false;
			}
		}else{
			var bstrErrorMessage = WTA_RemoteCall0.bstrErrorMessage;
			WTA_RemoteCall0.RC_Disconnect();	//연결종료
			ReleaseCapture();
			SetWaitCursor(false);
			//오류 코드 경고창
			alert("에러 메세지=======>" + bstrErrorMessage);
			return false;
		}
	//접속실패시
	}else{
		var ulErrorCode = WTA_RemoteCall0.ulErrorCode;
		WTA_RemoteCall0.RC_Disconnect();	//연결종료
		ReleaseCapture();
		SetWaitCursor(false);
		//오류 코드 경고창
		alert("접속 실패 오류=======>" + ulErrorCode);
		return false;
	}
}
/********************************************************************** 
 * 내용설명 : Transaction 리턴후 실행 함수
 * 주요변수 : 
 * 			- sMethodName	Transaction 고유 ID
 * 			- ErrorCode 	에러코드
 * 			- ErrorMSG 		에러 메세지
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");

	}else 
	{
		switch (svcId){
		case 'getOCAZ200C_tab1' :
			if (ds_OCAZ200C_tab1.GetColumn(0,"C1")=="0" or ds_OCAZ200C_tab1.GetColumn(0,"C2")=="0" or ds_OCAZ200C_tab1.GetColumn(0,"C3")!="0") {
				alert("사입,유상사급,가매상 마감작업이 진행되지 않았습니다. 다음으로 진행할 수 없습니다.");
			}else{
				alert("이상없습니다. 다음 작업을 진행바랍니다.");
				step_Flag = "tab1";
			}
			break;
		case 'getOCAZ200C_tab4' :
			if (Abs(toNumber(ds_OCAZ200C_tab4.GetColumn(0,"C3"))) > 90) {
				alert("사입,유상사급,가매상 마감작업이 진행되지 않았습니다. 다음으로 진행할 수 없습니다.");
			}else if (Abs(toNumber(ds_OCAZ200C_tab4.GetColumn(0,"C6"))) > 90) {
				alert("사입,유상사급,가매상 마감작업이 진행되지 않았습니다. 다음으로 진행할 수 없습니다.");
			}else if (Abs(toNumber(ds_OCAZ200C_tab4.GetColumn(0,"C9"))) > 90) {
				alert("사입,유상사급,가매상 마감작업이 진행되지 않았습니다. 다음으로 진행할 수 없습니다.");
			}else{
				alert("이상없습니다. 다음 작업을 진행바랍니다.");
				step_Flag = "tab4";
			}
			break;
		}
	}
}


