function fn_Search()
{
	getOBMZ140();
}

function fn_Input()
{
	if(checkInsertCount("ds_OBMZ140")== false) return;
	if(ds_OBMZ140.rowcount < 1) fn_Search();  
	ds_OBMZ140.AddRow();
	ds_OBMZ140.SetColumn(ds_OBMZ140.currow,"COMPS",ComboBoxPlant.Value);
}

function fn_Delete()
{
	if(ds_OBMZ140.rowcount < 1) return;			//삭제가 없으면 건너뛰기		
	ds_OBMZ140.DeleteRow(ds_OBMZ140.currow);	//삭제		
	alert(setPopMessage("1011"));				//[알림] 화면에서만 제거되었습니다. 저장하시면 삭제됩니다.		
	setMessage("1011");
}

function fn_Save()
{
	setOBMZ140();
}

