var strSeparator = "	";
var Lib1 = G_ds_user.GetColumn(0,"LIB1"); // cigma library(pereslib,psreslib)
var Lib2 = G_ds_user.GetColumn(0,"LIB2"); // 회사 library(psedblib,pscdblib)
var Lib3 = G_ds_user.GetColumn(0,"LIB3"); // 사업부 library(psedblib,pscdblib,pshdblib,pssdblib)
var company = G_ds_user.GetColumn(0,"COMPANY");
var comps = G_ds_user.GetColumn(0,"PLANT_CD");


function on_LoadCompleted(obj){
	getTitle();
	getDesign();
	getComCode();	
	gFcOnTopButtonColor("2.2"); //상단 버튼 활성화 여부 (기본:0,조회:1,변경:2)
	getLINCD();	//LINE코드조회

}

function getComCode(){

	http.Sync 	= true;
	var sKind		= "getComCode";						
	var sMethodName = "service::login/selectComCode.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ComCode_PLNT=ds_ComCode_PLNT";
	var sArgument   = "";
		sArgument   += " CLASS='PLNT'";
		sArgument   += " CODE1=" + quote(company);		
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	ComboBoxPlant.Value=comps;
	ComboBoxPlant.Enable = false; 	
}

function getDesign(){
	//MainGrid.ToolTipText = setPopMessage("2053");
	MainGrid1.BkColor = G_ds_design.GetColumn(0,"BkColor");
	MainGrid1.BkColor2 = G_ds_design.GetColumn(0,"BkColor2");
	MainGrid1.BkSelColor = G_ds_design.GetColumn(0,"BkSelColor");
	MainGrid1.Face3dColor = G_ds_design.GetColumn(0,"Face3dColor");
	MainGrid1.BoldHead = G_ds_design.GetColumn(0,"BoldHead");
	MainGrid.HeadHeight = G_ds_design.GetColumn(0,"HeadHeight");  //22
	//MainGrid.Width = G_ds_design.GetColumn(0,"Width");            //1048
	MainGrid1.Top = G_ds_design.GetColumn(0,"Top");                //64
	MainGrid1.UserData = MainGrid.Height;

	//MainGrid2.ToolTipText = setPopMessage("2053");
	MainGrid2.BkColor = G_ds_design.GetColumn(0,"BkColor");
	MainGrid2.BkColor2 = G_ds_design.GetColumn(0,"BkColor2");
	MainGrid2.BkSelColor = G_ds_design.GetColumn(0,"BkSelColor");
	MainGrid2.Face3dColor = G_ds_design.GetColumn(0,"Face3dColor");
	MainGrid2.BoldHead = G_ds_design.GetColumn(0,"BoldHead");
	MainGrid2.HeadHeight = G_ds_design.GetColumn(0,"HeadHeight");  //22
	//MainGrid.Width = G_ds_design.GetColumn(0,"Width");            //1048
	MainGrid2.Top = G_ds_design.GetColumn(0,"Top");                //64
	MainGrid2.UserData = MainGrid.Height;

	searchBar.Width = G_ds_design.GetColumn(0,"Width");           //1048
	searchBar.Top = G_ds_design.GetColumn(0,"SBTop");	          //32
	//searchBar.Height = G_ds_design.GetColumn(0,"SBHeight");	      //29
}

/********************************************************************** 
 * 내용설명 : 라인코드 조회
 **********************************************************************/ 
function getLINCD(){
	var sKind		= "getLINCD";						
	var sMethodName = "service::psogmlib/selectMSTCODE.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                            				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_LINCD=ds_MSTCODE";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
		sArgument += " LIB2=" + quote(Lib2);
		sArgument += " COMPS=" + quote(ComboBoxPlant.Value); //코드
		sArgument += " SQLID=" + "selectMSTLINCD";
	http.Sync=true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync = false;
}
/********************************************************************** 
 * 내용설명 : 화면 조회 
 **********************************************************************/ 
function getOBMZ150(){

	if(!validationChk()){
		return;
	}
	ds_OBMZ150.ClearData();

	var sKind		= "getOBMZ150";						
	var sMethodName = "service::psogmlib/selectMSTCODE.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                            				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_MSHNO=ds_MSTCODE";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
		sArgument += "@LIB1=" + quote(Lib1);
		sArgument += "@LIB2=" + quote(Lib2);
		sArgument += "@LIB3=" + quote(Lib3);
		sArgument += "@COMPS=" + quote(ComboBoxPlant.Value);
		sArgument += "@LINCD=" + quote(cmb_LINCD.Value); //라인코드
		sArgument += "@SQLID=" + quote("selectMSHNO"); //SQL명
	var str = dialog("Common::PGMLOAD_OPG.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");	
	http.Sync = false;

	if (str == 'T'){  //정상조회
		alert(setPopMessage("1004"));
		setMessage("1004");
		getOBMZ150_A();
	}else if (str == 'B'){   //data값이 없을때
		alert(setPopMessage("1003"));
		setMessage("1003");	
	}else if (str == 'E'){   //error발생시
		alert(setPopMessage("1002"));
		setMessage("1002");	
	}
}

function getOBMZ150_A()
{
	//하단 라인 조회
	var sKind		= "getOBMZ150_A";						
	var sMethodName = "service::psogmlib/selectOBMZ150.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_OBMZ150=ds_OBMZ150";
	var sArgument   = "";
		sArgument += " LIB2=" + quote(Lib2);
		sArgument += " COMPS=" + quote(ComboBoxPlant.Value); //사업부	
		sArgument += " LINCD=" + quote(cmb_LINCD.Value); //라인코드
		sArgument += " MSHNO=" + quote(ds_MSHNO.GetColumn(ds_MSHNO.currow,"CD")); //설비코드
	http.Sync 	= true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync 	= false;
}

/********************************************************************** 
 * 내용설명 : insert, update, delete
 **********************************************************************/ 
 function setOBMZ150(){
/*
	if(ds_OBMZ150.GetRowType(ds_OBMZ150.currow)=="update" ||ds_OBMZ150.GetRowType(ds_OBMZ150.currow)=="insert" ){
		if(!ds_OBMZ150_CanRowPosChange()){
			return;
		}
	}
*/
	var sKind		= "setOBMZ150";						
	var sMethodName = "service::psogmlib/saveOBMZ150.do";    // 서버에서 호출 될 Method 명
	var sInDataSet  = "ds_OBMZ150=ds_OBMZ150:U";               //자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_OBMZ150_CHECK=ds_OBMZ150_CHECK";  	   // 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
		//sArgument += " LIB1=" + quote(Lib1);
		sArgument += " LIB2=" + quote(Lib2);
		sArgument += " LINCD=" + quote(cmb_LINCD.Value); //라인코드
		sArgument += " MSHNO=" + quote(ds_MSHNO.GetColumn(ds_MSHNO.currow,"CD")); //설비코드
		sArgument += " USER_ID=" + quote(G_ds_user.GetColumn(0,"USER_ID"));
	http.Sync=true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
	http.Sync=false;

}
/********************************************************************** 
 * 내용설명 : Transaction 리턴후 실행 함수
 * 주요변수 : 
 * 			- sMethodName	Transaction 고유 ID
 * 			- ErrorCode 	에러코드
 * 			- ErrorMSG 		에러 메세지
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	if (ds_OBMZ150_CHECK.GetRowCount() > 0){
		for(var i=0;i<ds_OBMZ150_CHECK.GetRowCount();i++){
			alert("ACCCD : " +ds_OBMZ150_CHECK.GetColumn(i,"ACCCD") + " = " + ds_OBMZ150_CHECK.GetColumn(i,"RTMSG"));
		}
		getOBMZ150_A();  //error항목이 존재시에는 재조회
		return;
	}
	// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");

	}else {
		alert(setPopMessage("1001"));
		setMessage("1001");	
	}
}

/********************************************************************** 
 * 내용설명 : validation 체크
 **********************************************************************/ 
function validationChk(){
	var result = true;
	cmb_LINCD.BKColor="white";

	if (cmb_LINCD.Value == ""){
		cmb_LINCD.BKColor="paleturquoise";
		alert(setPopMessage("2543"));//[경고] 필수입력항목입니다. (라인코드)
		setMessage("2543");
		result = false;
	}
	return result;
}

/********************************************************************** 
 * 내용설명 : 엑셀파일로 저장
 **********************************************************************/ 
function setExcel()
{
	var fName   = "발생재해MASTER";
	var arrSheets  =  Array();  

	arrSheets[0]  = "MainGrid|발생재해MASTER";
	createExcelSheetS2(FileDialog0, fName ,arrSheets);
}

