function fn_Input()
{
	if(checkInsertCount("ds_OBMZ150")== false) return;
	if(ds_MSHNO.rowcount < 1) fn_Search();  
	ds_OBMZ150.AddRow();
	ds_OBMZ150.SetColumn(ds_OBMZ150.currow,"COMPS", ComboBoxPlant.Value);
	ds_OBMZ150.SetColumn(ds_OBMZ150.currow,"LINCD", cmb_LINCD.Value);
	ds_OBMZ150.SetColumn(ds_OBMZ150.currow,"MSHNO", ds_MSHNO.GetColumn(ds_MSHNO.currow,"CD"));
}

