function fn_Search()
{
	getOBMZ160();
}

function fn_Input()
{
	if(!validationChk()){
		return;
	}
	if(ds_OBMZ160.rowcount < 1) {
		fn_Search();	
	}
	ds_OBMZ160.AddRow();
	ds_OBMZ160.SetColumn(ds_OBMZ160.currow,"COMPS",ComboBoxPlant.Value);
}

function fn_Delete()
{
	if(ds_OBMZ160.rowcount < 1) return;			//삭제가 없으면 건너뛰기		
	ds_OBMZ160.DeleteRow(ds_OBMZ160.currow);	//삭제		
	alert(setPopMessage("1011"));	
	setPopMessage("1011");
}

function fn_Save()
{
	setOBMZ160();
}

