function ds_OBMZ160_CanColumnChange(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	http.usewaitcursor = false;
	if (strColumnID == "SABUN"){//작업자 확인
		if(varNewValue!=""){	
			//인사마스터
			http.Sync = true;
			var sKind		= "getHMD020PF";		
			var sMethodName = "service::pscomlib/selectINSAMasterPopup.do"; 
			var sInDataSet  = "";
			var sOutDataSet = "ds_HMD020PF=ds_Select";
			var sArgument   = "";
				sArgument  += " LIB2=" + quote(Lib2);
				sArgument  += " LIB3=" + quote(Lib3);
				sArgument  += " COMPS=" + quote(ComboBoxPlant.Value); //사업부
				sArgument  += " SABUN=" + quote(varNewValue);
			Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
			http.Sync = false;

			if(ds_HMD020PF.rowcount<1){
				alert("[알림]미등록된 사번입니다.");
				return false;
			}
			KORNM=ds_HMD020PF.GetColumn(0,"A2");
		}
	}
}
function ds_OBMZ160_CanRowPosChange(obj,nOldRow,nRow)
{
	http.usewaitcursor = false;

	var p_SABUN=ds_OBMZ160.GetColumn(ds_OBMZ160.currow,"SABUN"); //학력코드
	//if(ds_OBMZ160.GetRowType(ds_OBMZ160.currow)=="update" ||ds_OBMZ160.GetRowType(ds_OBMZ160.currow)=="insert" ){
		if(length(trim(p_SABUN))==0){	
			ds_OBMZ160.DeleteRow(ds_OBMZ160.currow);
			//return false;
		}
	//}
	return true;
	http.usewaitcursor = true;
}
function ds_OBMZ160_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	http.usewaitcursor = false;
	if (strColumnID == "SABUN"){
		if(varNewValue!=""){
			ds_OBMZ160.SetColumn(nRow, "KORNM", KORNM); // 작업자성명
		}else{
			ds_OBMZ160.SetColumn(nRow, "KORNM", ""); // 작업자성명
		}
	}
}
