function setExcel()
{
	var fName   = "설총율 기준정보관리";
	var arrSheets  =  Array();  

	arrSheets[0]  = "MainGrid|설총율 기준정보관리";
	createExcelSheetS2(FileDialog0, fName ,arrSheets);
}

